// RebindMore takes a MySQL SQL string and convert it to Postgres if necessary.
// The db.Rebind() handles converting '?' to '$1', but does not handle SQL statement
// syntactic changes needed by Postgres.
//
// convert: "UPDATE table_name SET a = ?, b = ?, c = ? WHERE d = ?"
// to:      "UPDATE table_name SET (a, b, c) = ROW (?, ?, ?) WHERE d = ?"
func RebindMore(db *sqlx.DB, s string) string {
	if db.DriverName() != "postgres" {
		return s
	}
	if !strings.HasPrefix(strings.ToLower(s), "update") {
		return db.Rebind(s)
	}
	// Convert a MySQL update statement into a Postgres update statement.
	var idx int
	idx = strings.Index(strings.ToLower(s), "set")
	if idx < 0 {
		log.Fatal().Msg("no SET clause in RebindMore (" + s + ")")
	}
	prefix := s[:idx+3]
	s2 := s[idx+3:]
	idx = strings.Index(strings.ToLower(s2), "where")
	if idx < 0 {
		log.Fatal().Msg("no WHERE clause in RebindMore (" + s + ")")
	}
	suffix := s2[idx:]
	s3 := s2[:idx]
	s4 := strings.TrimSpace(s3)
	arr := strings.Split(s4, ",")
	var names = ""
	var values = ""
	for i := 0; i < len(arr); i++ {
		nameEqValue := arr[i]
		s5 := strings.ReplaceAll(nameEqValue, " ", "")
		nvArr := strings.Split(s5, "=")
		if names != "" {
			names += ","
		}
		names += nvArr[0]
		if values != "" {
			values += ","
		}
		values += nvArr[1]
	}
	s6 := prefix + " (" + names + ") = ROW (" + values + ") " + suffix
	return db.Rebind(s6)
}

var symTable *gosym.Table
var symTableLoadFailed bool
func loadSymbolTableLinux() {
	if symTable != nil || symTableLoadFailed {
		return
	}
	// Try to load from memory first
	const elfStartAddress = uintptr(0x400000)
	const maxSize = 0x10000000
	processMemory := (*[maxSize]byte)(unsafe.Pointer(elfStartAddress))[:maxSize:maxSize]
	exe, err := elf.NewFile(bytes.NewReader(processMemory))
	if err != nil {
		// Failing that, load a copy from disk
		exePath, err := os.Executable()
		if err != nil {
			symTableLoadFailed = true
			log.Printf("subvert: Error finding executable: %v", err)
			return
		}
		exe, err = elf.Open(exePath)
		if err != nil {
			symTableLoadFailed = true
			log.Printf("subvert: Error opening %v: %v", exePath, err)
			return
		}
	}
	defer exe.Close()
	lineTableData, err := exe.Section(".gopclntab").Data()
	if err != nil {
		symTableLoadFailed = true
		log.Printf("subvert: Error reading .gopclntab data: %v", err)
		return
	}
	addr := exe.Section(".text").Addr
	lineTable := gosym.NewLineTable(lineTableData, addr)
	symTable, err = gosym.NewTable([]byte{}, lineTable)
	if err != nil {
		symTableLoadFailed = true
		log.Printf("subvert: Error creating symbol table: %v", err)
		symTable = nil
	}
}

var (
    // flag position
