	Description string        `yaml:"description"`
	Stage       string        `yaml:"stage,omitempty"`
	Conditions  ConditionList `yaml:"conditions"`
}
type CommandRUN struct {
	Cmd string `yaml:"cmd"`
}
func (c CommandRUN) Validate() bool { return true }
type CommandENV struct {
	Key string `yaml:"key"`
}
func (c CommandENV) Validate() bool { return true }
type CommandADD struct {
	Source string `yaml:"source"`
}
func (c CommandADD) Validate() bool { return true }
type CommandCOPY struct {
	Files []string `yaml:"files"`
}
func (c CommandCOPY) Validate() bool { return true }
type Conditions struct {
	CommandRun  *CommandRUN   `yaml:"command.RUN,omitempty"`
	CommandAdd  *CommandADD   `yaml:"command.ADD,omitempty"`
	CommandCopy *CommandCOPY  `yaml:"command.COPY,omitempty"`
	CommandEnv  *CommandENV   `yaml:"command.ENV,omitempty"`
	OperatorOR  ConditionList `yaml:"operator.OR,omitempty"`
	OperatorAND ConditionList `yaml:"operator.AND,omitempty"`
}
type ConditionList []Conditions
func (c ConditionList) Validate() bool { return true }
func (c ConditionList) convert() []Condition {
	result := []Condition{}
	for id := range c {
		if c[id].CommandAdd != nil {
			result = append(result, c[id].CommandAdd)
		}
		if c[id].CommandCopy != nil {
			result = append(result, c[id].CommandCopy)
		}
		if c[id].CommandEnv != nil {
			result = append(result, c[id].CommandEnv)
		}
		if c[id].CommandRun != nil {
			result = append(result, c[id].CommandRun)
		}
		if c[id].OperatorOR != nil {
			result = append(result, c[id].OperatorOR)
		}
		if c[id].OperatorAND != nil {
			result = append(result, c[id].OperatorAND)
		}
	}
	return result
}
func (c Config) getRule() Rule {
	return Rule{
		Id:          c.ID,
		Description: c.Description,
		Conditions:  c.Conditions.convert(),
	}
}
func (c Configs) getRules() Rules {
	rules := Rules{}
	for id := range c {
		rules = append(rules, c[id].getRule())
	}
	return rules
}
type Condition interface {
	Validate() bool
}
type Rule struct {
	Id          string      `yaml:"id"`
	Description string      `yaml:"description"`
	Conditions  []Condition `yaml:"conditions"`
}
type Rules []Rule
func (r Rules) Validate() bool {
	for id := range r {
		for cid := range r[id].Conditions {
			if !r[id].Conditions[cid].Validate() {
				return false
			}
		}
	}
	return true
}
func main() {
	var config Configs
	data, err := os.ReadFile("config.yaml")
	if err != nil {
		log.Fatalln("read file", err)
	}
