	data, err = yaml.Marshal(config)
	if err != nil {
		log.Fatalln("remarshal", err)
	}
	log.Printf("config:\n%s", string(data))
	rules := config.getRules()
	log.Printf("rule validation: %t\n", rules.Validate())
}

