func recursiveCount(arranged []*NestedComment) int {
	count := len(arranged)
	for _, item := range arranged {
		count += recursiveCount(item.Children)
	}
	return count
}
// Nesting function -------------------------------------------------------------------------
func ArrangeComments(comments []Comment) []*NestedComment {
	// Map to hold references to NestedComment by their ID
	queue := make(map[string]*NestedComment)
	// Result slice
	var result []*NestedComment
	// First, create all NestedComments and store references in the map
	for _, comment := range comments {
		nc := &NestedComment{Comment: comment}
		queue[comment.ID] = nc
	}
	// Second, loop through the map and add children to their parents
	for _, comment := range queue {
		if comment.ParentID != "" {
			parent := queue[comment.Comment.ParentID]
			if parent != nil {
				parent.Children = append(parent.Children, comment)
			}
		}
	}
	// Third, loop through the map and add all root comments to the result slice
	for _, comment := range queue {
		if comment.ParentID == "" {
			result = append(result, comment)
		}
	}
	return result
}

type Merger[T any] interface {
	Merge(override T) (T, error)
}
func asMerger(
	value reflect.Value,
) (
	mergeFn func(override reflect.Value) (reflect.Value, error),
	isMerger bool,
) {
	vType := value.Type()
	for i := 0; i < vType.NumMethod(); i++ {
		method := vType.Method(i)
		if method.Name != "Merge" {
			continue
		}
		methodType := method.Type
		if methodType.NumIn() != 2 {
			return nil, false
		}
		if methodType.NumOut() != 2 {
			return nil, false
		}
		if methodType.In(1) != vType {
			return nil, false
		}
		if methodType.Out(0) != vType {
			return nil, false
		}
		if methodType.Out(1) != reflect.TypeOf((*error)(nil)).Elem() {
			return nil, false
		}
		return func(override reflect.Value) (merged reflect.Value, err error) {
			// call method
			results := method.Func.Call([]reflect.Value{value, override})
			if !results[1].IsZero() && !results[1].IsNil() {
				err = results[1].Interface().(error)
			}
			return results[0], err
		}, true
	}
	return nil, false
}

