type Job struct {
	ID       int       `db:"id" json:"id"`
	Title    string    `db:"title" json:"title"`
	Body     string    `db:"body" json:"body"`
	Date     time.Time `db:"date" json:"date"`
	Titles   [4]string `db:"titles" json:"titles"`
	Contents [4]string `db:"contents" json:"contents"`
}
func main() {
	dsn := "host=localhost user=postgres password=postgres dbname=postgres port=5432 sslmode=disable"
	db, err := sql.Open("postgres", dsn)
	if err != nil {
		panic(err)
	}
	r := gin.Default()
	r.POST("/job", func(c *gin.Context) {
		var job Job
		if err := c.ShouldBindJSON(&job); err != nil {
			c.JSON(http.StatusBadRequest, gin.H{"error": err.Error()})
			return
		}
		// insert job into database
		query := "INSERT INTO jobs (title, body,  titles, contents ,date) VALUES ($1, $2, $3,  $4, $5) RETURNING id"
		var id int
		err := db.QueryRow(query, job.Title, job.Body, pq.Array(job.Titles), pq.Array(job.Contents), time.Now()).Scan(&id)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "failed to create job"})
			return
		}
		job.ID = id
		c.JSON(http.StatusOK, job)
	})
	r.GET("/jobs", func(c *gin.Context) {
		// retrieve all jobs from database
		rows, err := db.Query("SELECT * FROM jobs")
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "failed to retrieve jobs"})
			return
		}
		defer rows.Close()
		// iterate over rows and store in slice of Jobs
		jobs := []Job{}
		for rows.Next() {
			var job Job
			err := rows.Scan(&job.ID, &job.Title, &job.Body, &job.Date, pq.Array(job.Titles[:]), pq.Array(job.Contents[:]))
			if err != nil {
				c.JSON(http.StatusInternalServerError, gin.H{"error": "failed to retrieve jobs"})
				return
			}
			jobs = append(jobs, job)
		}
		c.JSON(http.StatusOK, jobs)
	})
	r.Run()
}

func (s *S) UnmarshalXML(d *xml.Decoder, start xml.StartElement) error {
	c := Child{}
	for {
		// Token returns the next XML token in the input stream.
        // At the end of the input stream, Token returns nil, io.EOF.
		t, _ := d.Token()
		switch tt := t.(type) {
		case xml.StartElement:
			c.TagName = tt.Name.Local
			if c.TagName == "a" {
				c.Toto = tt.Attr[0].Value
			}
			if c.TagName == "b" {
				c.Data = tt.Attr[0].Value
			}
		case xml.CharData:
			c.Value = string(tt)
		case xml.EndElement:
			if tt.Name.Local == c.TagName {
				s.Fields = append(s.Fields, c)
				c = Child{}
			}
		case nil:
			return nil
		}
	}
}

