type tHelper interface {
	Helper()
}
// didPanic returns true if the function passed to it panics. Otherwise, it returns false.
func didPanic(f assert.PanicTestFunc) (didPanic bool, message interface{}, stack string) {
	didPanic = true
	defer func() {
		message = recover()
		if didPanic {
			stack = string(debug.Stack())
		}
	}()
	// call the target function
	f()
	didPanic = false
	return
}
func PanicsWithLogrusError(t assert.TestingT, errString string, f assert.PanicTestFunc, msgAndArgs ...interface{}) bool {
	if h, ok := t.(tHelper); ok {
		h.Helper()
	}
	funcDidPanic, panicValue, panickedStack := didPanic(f)
	if !funcDidPanic {
		return assert.Fail(t, fmt.Sprintf("func %#v should panic\n\tPanic value:\t%#v", f, panicValue), msgAndArgs...)
	}
	panicErr, ok := panicValue.(*logrus.Entry)
	if !ok || panicErr.Message != errString {
		return assert.Fail(t, fmt.Sprintf("func %#v should panic with logrus error message:\t%#v\n\tPanic value:\t%#v\n\tPanic stack:\t%s", f, errString, panicErr.Message, panickedStack), msgAndArgs...)
	}
	return true
}

func ReadFromStdinIfAvailable(cmd *cobra.Command, args []string) ([]byte, error) {
	if len(args) == 0 {
		r := bufio.NewReader(RootCmd.InOrStdin())
		var bytesResult []byte
		scanner := bufio.NewScanner(r)
		// buffered channel of dataStream
		dataStream := make(chan []byte, 1)
		// Run scanner.Bytes() function in it's own goroutine and pass back it's
		// response into dataStream channel.
		go func() {
			for scanner.Scan() {
				dataStream <- scanner.Bytes()
			}
			close(dataStream)
		}()
		// Listen on dataStream channel AND a timeout channel - which ever happens first.
		timedOut := false
		select {
		case res := <-dataStream:
			bytesResult = append(bytesResult, res...)
		case <-time.After(time.Duration(10) * time.Millisecond): //nolint:gomnd // 10ms timeout
			timedOut = true
		}
		if timedOut {
			cmd.Println(userstrings.NoFilenameProvidedErrorString)
			return nil, errors.New(userstrings.NoFilenameProvidedErrorString)
		}
		for scanner.Scan() {
			bytesResult = append(bytesResult, scanner.Bytes()...)
		}
		return bytesResult, nil
	}
	return nil, fmt.Errorf("should not be possible, args should be empty")
}

