func saveAsJSONWithHeaders(rows [][]string, filename string) error {
	data := make([]map[string]string, len(rows)-1)
	headers := rows[0]
    // excluding header row
	for i, row := range rows[1:] {
		data[i] = make(map[string]string)
		for j, cellValue := range row {
			data[i][headers[j]] = cellValue
		}
	}
	return saveAsJSON(data, filename)
}
func saveAsJSON(data interface{}, filename string) error {
	file, err := os.Create(filename)
	if err != nil {
		return err
	}
	defer file.Close()
	encoder := json.NewEncoder(file)
	if err := encoder.Encode(data); err != nil {
		return err
	}
	return nil
}

goroutine 1                                 goroutine 2
                                            1. if c.isFinished() {
                                                   return fmt.Errorf("counter is finished")
                                               }
2. if !c.isFinished() {
3.     atomic.StoreUint32(&c.finished, 1)
4.     atomic.StoreUint64(&c.counter, 0)
   }
                                            5. atomic.AddUint64(&c.counter, 1)
                                            6. return nil

type Counter struct {
	counter  uint64
	finished uint32
	m        sync.Mutex
}
// Inc increments the counter by one
func (c *Counter) Inc() error {
	c.m.Lock()
	defer c.m.Unlock()
	if c.finished != 0 {
		return fmt.Errorf("counter is finished")
	}
	c.counter++
	return nil
}
// Dec decrements the counter by one, but prevents the counter from going to zero
func (c *Counter) Dec() {
	c.m.Lock()
	defer c.m.Unlock()
	// prevent overflow
	if c.counter > 0 {
		c.counter--
	}
}
// Cancel sets the finished flag, and sets counter to zero
func (c *Counter) Cancel() {
	c.m.Lock()
	defer c.m.Unlock()
	if c.finished == 0 {
		c.finished = 1
		c.counter = 0
	}
}

