import "fmt"
func main() {
	u := append([]int{1, 2}, 3)
	// The length is 3 but the capacity is 4.
	fmt.Printf("u %v\n  len: %d\n  cap: %d\n", u, len(u), cap(u))
	// Since u has enough capacity for the new element "4",
	// v1 will share the same underlying array.
	v1 := append(u, 4)
	fmt.Println("v1:", v1)
	// As what happened to v1, v2 will share the same underlying array too.
	// But the last element "4" in the underlying array is changed to "5".
	v2 := append(u, 5)
	fmt.Println("v2:", v2)
	// Since v1 uses the same underlying array, it sees the change in the last step.
	fmt.Println("v1:", v1)
}

// Job represents a unit of work to be processed by a worker.
type Job struct {
	r    *http.Request       // HTTP request to be processed
	w    http.ResponseWriter // Response writer to send the result
	done chan struct{}       // Closed when write competed
}
...
func handler(w http.ResponseWriter, r *http.Request) {
	// Create a job with the request and response.
	job := &Job{r, w, make(chan struct{})}
	// Push the job onto the queue.
	q.Push(job)
	log.Println("Received request and added job to queue")
	<-job.done // Wait until job has been processed
}
...
// worker processes jobs from the queue.
func worker() {
	for {
		job, ok := q.Pop()
		if ok {
			log.Println("Worker processing job")
			doWork(job)
			close(job.done) // Notify requester that we are done
		}
	}
}

