func Consume(in, out chan int, err chan error) {
	defer close(out)
	defer close(err)
	for i := range in {
		if i%2 == 0 {
			out <- i
		} else {
			err <- errors.New("we don't like odd numbers")
		}
	}
}
func main() {
	in := make(chan int)
	out := make(chan int)
	err := make(chan error)
	go func(in chan int) {
		defer close(in)
		for i := 0; i < 10; i++ {
			fmt.Println("  input", i)
			in <- i
		}
	}(in)
	go Consume(in, out, err)
	for {
		select {
		case i, ok := <-out:
			if !ok {
				return
			}
			fmt.Printf("Processed Data %d\n", i)
		case er, ok := <-err:
			if !ok {
				return
			}
			if er != nil {
				fmt.Println(er)
			}
		}
	}
}

