)
func ConvertFieldName(s string) string {
	r := regexp.MustCompile("(\\b|-|_|\\.)[a-z]")
	return r.ReplaceAllStringFunc(s, func(t string) string {
		if len(t) == 1 {
			return strings.ToUpper(t)
		} else {
			return strings.ToUpper(string(t[1]))
		}
	})
}
func main() {
	fmt.Println(ConvertFieldName("debug"))
	fmt.Println(ConvertFieldName("a.string"))
	fmt.Println(ConvertFieldName("a-string"))
	fmt.Println(ConvertFieldName("a_string"))
	fmt.Println(ConvertFieldName("-a.string"))
	fmt.Println(ConvertFieldName("something-more-complex"))
}

func batchCollector(ctx context.Context, batchSize int) error {
    prodCh, errCh := concurrentProducer(ctx)
    batch := make([]int, 0, batchSize)
    for value:=range prodCh {
        batch = append(batch, elem)
        if len(batch) < batchSize {
            continue
         }
         batchConsumer(batch)
         batch = batch[:0]
     }
     // If error happened, throw away accumulated batch
     // Check nonzero batch before error if you don't want to lose batches
     var err error
     select {
       case err=<-errCh:
       default:
     }
     if len(batch)>0 {
        batchConsumer(batch)
     }
    return err
}

