		scanMain := strings.Fields(userIn)
		if len(scanMain) == 0 {
			break
		}
		if len(scanMain) != 4 {
			fmt.Println("Invalid Input. Try Again.")
		}
		Scan0 := scanMain[0]
		Scan1 := scanMain[1]
		Scan2 := scanMain[2]
		Scan3 := scanMain[3]
		if Scan0 == "car" {
			Cars = append(Cars, Car{Scan1, Scan2, Scan3})
