import "google.golang.org/protobuf/encoding/protowire"
func getData(src []byte) (creationTime, expiryTime uint64, err error) {
	remaining := src
	for len(remaining) > 0 {
		fieldNum, wireType, n := protowire.ConsumeTag(remaining)
		if n < 0 {
			return 0, 0, fmt.Errorf("failed to consume tag: %w", protowire.ParseError(n))
		}
		remaining = remaining[n:]
		switch fieldNum {
		case 4: // Expiry time
			if wireType != protowire.VarintType {
				return 0, 0, fmt.Errorf("unexpected type for expiry time field: %d", wireType)
			}
			expiryTime, n = protowire.ConsumeVarint(remaining)
		case 5: // Creation time
			if wireType != protowire.VarintType {
				return 0, 0, fmt.Errorf("unexpected type for creation time field: %d", wireType)
			}
			creationTime, n = protowire.ConsumeVarint(remaining)
		default:
			n = protowire.ConsumeFieldValue(fieldNum, wireType, remaining)
		}
		if n < 0 {
			return 0, 0, fmt.Errorf("failed to consume value for field %d: %w", fieldNum, protowire.ParseError(n))
		}
		remaining = remaining[n:]
	}
	return
}

func (p *Pool) getConfig() (*pgxpool.Config, error) {
	config, err := pgxpool.ParseConfig("")
	if err != nil {
		return nil, err
	}
	/**
	This will refresh the password if the connection is expired
	*/
	config.BeforeConnect = func(ctx context.Context, config *pgx.ConnConfig) error {
		if p.isTokenExpired() {
			creds, err := p.getCredentials()
			if err != nil {
				return err
			}
			config.User = creds.user
			config.Password = creds.password
			config.Host = creds.host
			config.Database = creds.database
			config.Port = creds.port
			p.cachedCredentials = creds
			p.expire = time.Now()
		} else {
			config.User = p.cachedCredentials.user
			config.Password = p.cachedCredentials.password
			config.Host = p.cachedCredentials.host
			config.Database = p.cachedCredentials.database
			config.Port = p.cachedCredentials.port
		}
		return nil
	}
	return config, nil
}

