var defaultTimeCodec = bsoncodec.NewTimeCodec()
var typeTime = reflect.TypeOf(time.Time{})
// DoubleToDateConverter converts bson doubles to time.Time
func DoubleToDateConverter(ctx bsoncodec.DecodeContext, vr bsonrw.ValueReader, val reflect.Value) error {
	if !val.CanSet() || val.Type() != typeTime {
		return errors.New("bad type or not settable")
	} else if vr.Type() == bson.TypeDouble {
		epochMilliVal, err := vr.ReadDouble()
		if err != nil {
			return err
		}
		timeVal := time.Unix(0, int64(epochMilliVal)*int64(time.Millisecond))
		val.Set(reflect.ValueOf(timeVal))
		return nil
	}
    // run the default codec if the value being read from the db is not a double
	return defaultTimeCodec.DecodeValue(ctx, vr, val)
}

package main
import (
    "fmt"
    "syscall"
    "unsafe"
    "golang.org/x/sys/windows"
)
var (
    psapi              = windows.NewLazySystemDLL("psapi.dll")
    procGetProcessMemoryInfo = psapi.NewProc("GetProcessMemoryInfo")
)
type PROCESS_MEMORY_COUNTERS struct {
    cb                         uint32
    PageFaultCount             uint32
    PeakWorkingSetSize         uintptr
    WorkingSetSize             uintptr
    QuotaPeakPagedPoolUsage    uintptr
    QuotaPagedPoolUsage        uintptr
    QuotaPeakNonPagedPoolUsage uintptr
    QuotaNonPagedPoolUsage     uintptr
    PagefileUsage              uintptr
    PeakPagefileUsage          uintptr
}
func GetProcessMemoryInfo(handle windows.Handle) (PROCESS_MEMORY_COUNTERS, error) {
    var memCounters PROCESS_MEMORY_COUNTERS
    memCounters.cb = uint32(unsafe.Sizeof(memCounters))
    r, _, err := procGetProcessMemoryInfo.Call(
        uintptr(handle),
        uintptr(unsafe.Pointer(&memCounters)),
        uintptr(memCounters.cb),
    )
    if r == 0 {
        return memCounters, err
    }
    return memCounters, nil
}
func main() {
    // pid:= windows.GetCurrentProcess()  previously
    pid := windows.CurrentProcess() // Get current process handle (new method name)
    memInfo, err := GetProcessMemoryInfo(pid)
    if err != nil {
        fmt.Printf("Error: %v\n", err)
        return
    }
    fmt.Printf("Memory Usage:\n")
    fmt.Printf("Working Set Size: %v bytes\n", memInfo.WorkingSetSize) // actual physical memory used by application
    fmt.Printf("Pagefile Usage: %v bytes\n", memInfo.PagefileUsage) // virtual memory
}

