func (r *ApplicationResource) Configure(ctx context.Context, req resource.ConfigureRequest, resp *resource.ConfigureResponse) {
	if req.ProviderData == nil {
		// IMPORTANT: This method is called MULTIPLE times. An initial call might not have configured the Provider yet, so we need
		// to handle this gracefully. It will eventually be called with a configured provider.
		return
	}
	client, ok := req.ProviderData.(*http.Client)
	if !ok {
		resp.Diagnostics.AddError(
			"Unexpected Resource Configure Type",
			fmt.Sprintf("Expected *http.Client, got: %T. Please report this issue to the provider developers.", req.ProviderData),
		)
		return
	}
	r.client = client
}

package main
import "fmt"
func main() {
	x := 2328399149
	fmt.Printf("%.0f", buildKeyArray(int32(x), 36, 142609045))
}
type Xor128Generator struct {
	SM int32
	tn int32
}
func NewXor128Generator(aT, Gj int32) *Xor128Generator {
	return &Xor128Generator{SM: aT, tn: Gj}
}
func (x *Xor128Generator) Generate() float64 {
	Fv := x.SM
	Fv ^= Fv << 23
	Fv ^= Fv >> 17
	RW := x.tn
	Fv ^= RW
	x.SM = RW
	Fv ^= RW >> 26
	x.tn = Fv
	return float64(x.SM) + float64(x.tn)
}
func buildKeyArray(XorSection int32, max int, Xor128Seed int32) []float64 {
	gen := NewXor128Generator(XorSection, Xor128Seed)
	outputArray := make([]float64, max)
	for index := 0; index < max; index++ {
		outputArray[index] = gen.Generate()
	}
	return outputArray
}

