import (
	"fmt"
	"hash/crc32"
	"math/bits"
	"github.com/snksoft/crc"
)
func crcSnksoft(params *crc.Parameters, input []byte) uint32 {
	h := crc.NewHash(params)
	h.Update(input)
	return h.CRC32()
}
func crcGo(poly uint32, input []byte) uint32 {
	reversedPoly := bits.Reverse32(poly)
	t := crc32.MakeTable(reversedPoly)
	return crc32.Checksum(input, t)
}
func main() {
	poly := 0xF4ACFB13
	data := []byte("0")
	params := &crc.Parameters{
		Width:      32,
		Polynomial: uint64(poly),
		Init:       0x00000000,
		ReflectIn:  false,
		ReflectOut: false,
		FinalXor:   0x00000000,
	}
	mimicGoParams := &crc.Parameters{
		Width:      32,
		Polynomial: uint64(poly),
		Init:       0xFFFFFFFF,
		ReflectIn:  true,
		ReflectOut: true,
		FinalXor:   0xFFFFFFFF,
	}
	fmt.Printf("snksoft/crc: 0x%X\n", crcSnksoft(params, data))
	fmt.Printf("snksoft/crc (matching hash/crc32): 0x%X\n", crcSnksoft(mimicGoParams, data))
	fmt.Printf("Go standard library (hash/crc32): 0x%X\n", crcGo(uint32(poly), data))
}

package main
import (
	"context"
	"fmt"
	"time"
	"golang.org/x/sync/errgroup"
)
func main() {
	const threads = 3
	ch := make(chan int, threads)
	eg, ctx := errgroup.WithContext(context.Background())
	for i := 0; i < threads; i++ {
		i := i
		eg.Go(func() error {
			fmt.Printf("Thread %d: STARTED\n", i)
			for n := range ch {
				fmt.Printf("Thread %d: GOT=%d\n", i, n)
				time.Sleep(time.Duration(1) * time.Second)
				// Simulate failed thread
				if n == 2 {
					fmt.Printf("Thread %d: FAILED\n", i)
					return fmt.Errorf("Thread %d: FAILED", i)
				}
				// For the purpose of this example we will just check the context after
				// doing the work.
				if ctx.Err() != nil {
					fmt.Printf("Thread %d: Quiting due to context\n", i)
					return fmt.Errorf("Thread %d: Context cancelled: ", i)
				}
			}
			return nil
		})
	}
	// We want to stop sending new work if there is a failure (otherwise `ch <- i` will block permanently);
sendLoop:
	for i := 0; i < 9; i++ {
		select {
		case ch <- i:
		case <-ctx.Done():
			fmt.Printf("stopped\n")
			break sendLoop // Just exit the loop if something goes wrong (you may want to do something else here)
		}
	}
	close(ch) // Note: Important that this happens even if there is an error (otherwise `for n := range ch` may block, leaking goroutines)
	fmt.Printf("all sent\n")
	if err := eg.Wait(); err != nil {
		panic(err)
	}
}

