func EncodeWithPublicKey(text string, publicKey string) (string, error) {
	// Decode the public key from base64
	publicKeyBytes, err := base64.StdEncoding.DecodeString(publicKey)
	if err != nil {
		return "", err
	}
	// Decode the public key
	var publicKeyDecoded [32]byte
	copy(publicKeyDecoded[:], publicKeyBytes)
	// Encrypt the secret value
	encrypted, err := box.SealAnonymous(nil, []byte(text), (*[32]byte)(publicKeyBytes), rand.Reader)
	if err != nil {
		return "", err
	}
	// Encode the encrypted value in base64
	encryptedBase64 := base64.StdEncoding.EncodeToString(encrypted)
	return encryptedBase64, nil
}

package main
import (
	"fmt"
	"strconv"
)
func main() {
	books := map[int]string{
		0: "wadidaw",
		1: "bededaw",
		2: "celepaw",
	}
	target := "books[1]"
	value, err := getValueByString(target, books)
	if err != nil {
		fmt.Println("Error:", err)
	} else {
		fmt.Println("Book title:", value)
	}
}
func getValueByString(target string, data map[int]string) (interface{}, error) {
	parts := splitTarget(target)
	if len(parts) != 2 {
		return nil, fmt.Errorf("invalid target format: %s", target)
	}
	// Get the variable name and index
	varName := parts[0]
	index, err := strconv.Atoi(parts[1])
	if err != nil {
		return nil, fmt.Errorf("invalid index: %s", parts[1])
	}
	// Check if the variable exists
	value, ok := data[index]
	if !ok {
		return nil, fmt.Errorf("variable not found: %s", varName)
	}
	return value, nil
}
func splitTarget(target string) []string {
	parts := make([]string, 2)
	// Find the index of the opening bracket
	braceIndex := -1
	for i, char := range target {
		if char == '[' {
			braceIndex = i
			break
		}
	}
	// Extract the variable name and index
	if braceIndex != -1 {
		parts[0] = target[:braceIndex]
		parts[1] = target[braceIndex+1 : len(target)-1]
	}
	return parts
}

package middlewares
import (
	"bytes"
	"encoding/json"
	"fmt"
	"net/http"
	"github.com/gin-gonic/gin"
	"github.com/gin-gonic/gin/binding"
	"github.com/sirupsen/logrus"
	log "github.com/sirupsen/logrus"
)
type ginBodyLogger struct {
	// get all the methods implementation from the original one
	// override only the Write() method
	gin.ResponseWriter
	body bytes.Buffer
}
func (g *ginBodyLogger) Write(b []byte) (int, error) {
	g.body.Write(b)
	return g.ResponseWriter.Write(b)
}
func RequestLoggingMiddleware(logger *logrus.Logger) gin.HandlerFunc {
	return func(ctx *gin.Context) {
		ginBodyLogger := &ginBodyLogger{
			body:           bytes.Buffer{},
			ResponseWriter: ctx.Writer,
		}
		ctx.Writer = ginBodyLogger
		var req interface{}
		if err := ctx.ShouldBindBodyWith(&req, binding.JSON); err != nil {
			ctx.JSON(http.StatusBadRequest, err.Error())
			return
		}
		data, err := json.Marshal(req)
		if err != nil {
			panic(fmt.Errorf("err while marshaling req msg: %v", err))
		}
		ctx.Next()
		logger.WithFields(log.Fields{
			"status":       ctx.Writer.Status(),
			"method":       ctx.Request.Method,
			"path":         ctx.Request.URL.Path,
			"query_params": ctx.Request.URL.Query(),
			"req_body":     string(data),
			"res_body":     ginBodyLogger.body.String(),
		}).Info("request details")
	}
}

