package main
import (
	"fmt"
	"io"
	"os"
	"time"
)
func main() {
	// os.Stdin.SetReadDeadline(time.Now().Add(2 * time.Second))
	b, err := readAll(os.Stdin)
	if err != nil {
		fmt.Println("ERROR: ", err.Error())
	}
	str := string(b)
	fmt.Println(str)
}
func readAll(r io.Reader) ([]byte, error) {
	b := make([]byte, 0, 512)
	i := 0
	for {
		if len(b) == cap(b) {
			// Add more capacity (let append pick how much).
			b = append(b, 0)[:len(b)]
		}
		n, err := r.Read(b[len(b):cap(b)])
		//fmt.Fprintf(os.Stderr, "READ %d - RECEIVED: \n%s\n", i, string(b[len(b):cap(b)]))
		fmt.Fprintf(os.Stderr, "%s READ %d - RECEIVED %d BYTES\n", time.Now(), i, n)
		i++
		b = b[:len(b)+n]
		if err != nil {
			if err == io.EOF {
				fmt.Fprintln(os.Stderr, "RECEIVED EOF")
				err = nil
			}
			return b, err
		}
	}
}

package main
import (
	"context"
	"log"
	"sync"
	"time"
)
type studentDetails struct {
	title string
	link  string
}
func main() {
	var wg sync.WaitGroup
	var err error
	students := make([]studentDetails, 0)
	studentCh := make(chan studentDetails, 10000)
	errorCh := make(chan error, 1)
	start := time.Now()
	wg.Add(1)
	go getDetailStudents(context.TODO(), studentCh, errorCh, &wg, "http://example.com", false)
LOOP:
	for {
		select {
		case p, ok := <-studentCh:
			if ok {
				log.Printf("Links %s: [%s]\n", p.title, p.link)
				students = append(students, p)
			} else {
				log.Println("Draining student channel")
				for p := range studentCh {
					log.Printf("Links %s: [%s]\n", p.title, p.link)
					students = append(students, p)
				}
				break LOOP
			}
		case err = <-errorCh:
			if err != nil {
				break LOOP
			}
		case <-wrapWait(&wg):
			close(studentCh)
		}
	}
	close(errorCh)
	elapsed := time.Since(start)
	log.Printf("operation took %s", elapsed)
}
func getDetailStudents(rCtx context.Context, content chan<- studentDetails, errorCh chan<- error, wg *sync.WaitGroup, url string, subSection bool) {
	defer func() {
		log.Println("Closing")
		wg.Done()
	}()
	if !subSection {
		wg.Add(1)
		go getDetailStudents(rCtx, content, errorCh, wg, url, true)
		// L.Warnf("total pages found %d", pageSub.Length()+1)
	}
	content <- studentDetails{
		title: "title",
		link:  "link",
	}
}
// helper function to allow using WaitGroup in a select
func wrapWait(wg *sync.WaitGroup) <-chan struct{} {
	out := make(chan struct{})
	go func() {
		wg.Wait()
		out <- struct{}{}
	}()
	return out
}

