	"github.com/gorilla/websocket"
)
func main() {
	wg := sync.WaitGroup{}
	wg.Add(2)
	// Server
	go func() {
		defer wg.Done()
		var (
			upgrader = &websocket.Upgrader{}
			stopChan = make(chan struct{})
		)
		http.HandleFunc("/ws", func(w http.ResponseWriter, r *http.Request) {
			defer func() {
				close(stopChan)
			}()
			upgrade, err := upgrader.Upgrade(w, r, nil)
			if err != nil {
				return
			}
			for {
				msgType, msg, err := upgrade.ReadMessage()
				if err != nil {
					log.Println("connection closed,", err)
					return
				}
				log.Println(msgType, string(msg))
			}
		})
		go func() {
			err := http.ListenAndServe("localhost:8081", nil)
			if err != nil {
				log.Fatalln(err)
			}
		}()
		<-stopChan
	}()
	// Dialer (Client)
	go func() {
		defer wg.Done()
		dial, _, err := websocket.DefaultDialer.Dial("ws://localhost:8081/ws", nil)
		if err != nil {
			log.Fatalln(err)
		}
		defer dial.Close()
		err = dial.WriteMessage(websocket.TextMessage, []byte("hello"))
		if err != nil {
			log.Fatalln(err)
		}
		err = dial.WriteMessage(websocket.TextMessage, []byte("world"))
		if err != nil {
			log.Fatalln(err)
		}
	}()
	wg.Wait()
}
