func checkService(name string, ip string) error {
	p, err := ping.NewPinger(ip)
	if err != nil {
		return err
	}
	p.Count = 3
	p.Timeout = 5 * time.Second
	err = p.Run()
	if err != nil {
		return err
	}
	stats := p.Statistics()
	if stats.PacketLoss == 100 {
		return fmt.Errorf("service %s down", name)
	}
	fmt.Printf("stats: %#v\n", stats)
	return nil
}

