func (b *Book) UnmarshalXML(d *xml.Decoder, start xml.StartElement) error {
	for {
		t, err := d.Token()
		if err != nil {
			if err != io.EOF {
				return err
			}
			return nil
		}
		switch t := t.(type) {
		case xml.StartElement:
			var f interface{} // field
			var r string      // replace
			switch t.Name.Local {
			case "title":
				f = &b.Title
			case "author":
				if len(b.Author) > 0 { // if "author" was already decoded then assume this is the element in the "blah chardata"
					r = b.Author // if you want <author/> to appear in Text then do `r = "<author/>"` instead
				} else {
					f = &b.Author
				}
			case "year":
				if len(b.Year) > 0 { // same logic as for author above
					r = b.Year
				} else {
					f = &b.Year
				}
			case "price":
				f = &b.Price
			}
			if f != nil {
				if err := d.DecodeElement(f, &t); err != nil {
					return err
				}
			}
			if len(r) > 0 {
				b.Text += " " + r + " " // add empty space for padding the replacement string
			}
		case xml.CharData:
			s := strings.TrimSpace(string(t))
			if len(s) > 0 {
				b.Text += s
			}
		}
	}
	return nil
}

func RemoveVehicle(w http.ResponseWriter, r *http.Request) {
    // check if the user is logged in
	username, _ := ExtractTokenUsername(r)
	if len(username) == 0 {
        // if not, render the template with no data, this
        // will show the "please login" part of your template
		if err := removeTemplate.Execute(w, nil); err != nil {
            http.Error(w, err.Error(), http.StatusInternalServerError)
		}
		return
	}
    switch r.Method {
	// if the method is GET, show the list of vehicles available
	case http.MethodGet:
		// select vehicles from db
		rows, err := db.Query(`SELECT * FROM Vehicle`)
		if err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
			return
		}
		defer rows.Close()
		// scan rows
		var vehicles []Vehicle
		for rows.Next() {
			var v Vehicle
			err := rows.Scan(&v.Id, &v.Date, &v.Brand, &v.Model, &v.Mileage, &v.Year, &v.rented, &v.Dayrate)
			if err != nil {
				http.Error(w, err.Error(), http.StatusInternalServerError)
				return
			}
			vehicles = append(vehicles, v)
		}
		if err := rows.Err(); err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
			return
		}
		// now render the template with the data
        // that you just retrieved from the database
		data := map[string]interface{}{
			"Username": username,
			"Vehicles": vehicles,
		}
		if err := removeTemplate.Execute(w, data); err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
		}
	// if the method is POST, delete the vehicle
	case http.MethodPost {
		var id = r.FormValue("select")
		if _, err := db.Exec("DELETE FROM vehicle WHERE id=?", id); err != nil {
			http.Error(w, err.Error(), http.StatusInternalServerError)
		}
	}
}

