import (
	"bufio"
	"bytes"
	"fmt"
	"os"
	"strings"
	"sync"
	"time"
)
type SleepResult struct {
	worker_id int
	duration  time.Duration
	start     time.Time
	end       time.Time
}
func main() {
	var num_workers = 2
	workchan := make(chan time.Duration)
	resultschan := make(chan SleepResult)
	var wg sync.WaitGroup
	var resultswg sync.WaitGroup
	resultswg.Add(1)
	go results(&resultswg, resultschan)
	for i := 0; i < num_workers; i++ {
		wg.Add(1)
		go worker(i, &wg, workchan, resultschan)
	}
	// playground doesn't have stdin
	var input = bytes.NewBufferString(
		strings.Join([]string{
			"3ms",
			"1 seconds",
			"3600ms",
			"300 ms",
			"5s",
			"0.05min"}, "\n") + "\n")
	var scanner = bufio.NewScanner(input)
	for scanner.Scan() {
		text := scanner.Text()
		if dur, err := time.ParseDuration(text); err != nil {
			fmt.Fprintln(os.Stderr, "Invalid duration", text)
		} else {
			workchan <- dur
		}
	}
	close(workchan) // we know when our inputs are done
	wg.Wait()       // and when our jobs are done
	close(resultschan)
	resultswg.Wait()
}
func results(wg *sync.WaitGroup, resultschan <-chan SleepResult) {
	for res := range resultschan {
		fmt.Printf("Worker %d: %s : %s => %s\n",
			res.worker_id, res.duration,
			res.start.Format(time.RFC3339Nano), res.end.Format(time.RFC3339Nano))
	}
	wg.Done()
}
func worker(id int, wg *sync.WaitGroup, jobchan <-chan time.Duration, resultschan chan<- SleepResult) {
	var res = SleepResult{worker_id: id}
	for dur := range jobchan {
		res.duration = dur
		res.start = time.Now()
		time.Sleep(res.duration)
		res.end = time.Now()
		resultschan <- res
	}
	wg.Done()
}

// write a marshal func that converts a given type to the structure declared above
// - this example converts only plain structs
func marshalOpenAPI(v interface{}) (interface{}, error) {
	rt := reflect.TypeOf(v)
	if rt.Kind() == reflect.Struct {
		obj := DataType{Type: "object"}
		for i := 0; i < rt.NumField(); i++ {
			f := rt.Field(i)
			tag := strings.Split(f.Tag.Get("xml"), ",")
			if len(tag) == 0 || len(tag[0]) == 0 { // no name?
				continue
			}
			if obj.Props == nil {
				obj.Props = make(map[string]*DataType)
			}
			name := tag[0]
			obj.Props[name] = &DataType{
				Type: goTypeToOpenAPIType(f.Type),
			}
			if len(tag) > 1 && tag[1] == "attr" {
				obj.Props[name].XML = &XML{Attr: true}
			}
		}
		return obj, nil
	}
	return nil, fmt.Errorf("unsupported type")
}

