func bytesSimilar2(s1 []byte, s2 []byte) []byte {
	l1 := len(s1)
	l2 := len(s2)
	least := l1
	if least > l2 {
		least = l2
	}
	count := 0
	for i := 0; i < least; i++ {
		if s1[i] == s2[i] {
			count++
			continue
		}
		break
	}
	if count == 0 {
		return []byte{}
	}
	return s1[:count]
}
