func removeOneElementSlice(data map[string]interface{}) {
	for k, v := range data {
		switch t := v.(type) {
		case []interface{}:
			if len(t) == 1 {
				data[k] = t[0]
				if v, ok := data[k].(map[string]interface{}); ok {
					removeOneElementSlice(v)
				}
			}
		}
	}
}

