func GetUserDetailsHander(c *gin.Context) {
	var userInfo USERINlFO
	var billingInfo BILLL
	err := parallel(
		func() (e error) {
			userInfo, e = UserRepository.FindById(c.getInt("user_id"))
			return
		},
		func() (e error) {
			billingInfo, e = BillingRepository.FindById(c.getInt("user_id"))
