	)
	fmt.Println(err)
	c.JSON(http.StatusOK, gin.H{
		user_data:    userResult,
		billing_data: billingInfo,
	})
	return
}
func parallel(do ...func() error) error {
	var err error
	rcverr := make(chan error)
	var wg sync.WaitGroup
	for _, d := range do {
		wg.Add(1)
		go func(do func() error) {
			rcverr <- do()
			wg.Done()
		}(d)
	}
	go func() {
		wg.Wait()
		close(rcverr)
	}()
	for range do {
		e := <-rcverr
		if e != nil {
			err = e // return here for fast path
		}
	}
	return err
}

