import (
	"context"
	"fmt"
	"os"
	admin "google.golang.org/api/admin/directory/v1"
	"google.golang.org/api/googleapi"
)
func main() {
	ctx := context.Background()
	adminService, err := admin.NewService(ctx)
	if err != nil {
		panic(err)
	}
	// Create a request
	rqst := adminService.Users.List().Customer("my_customer")
	// Loop
	for {
		// Do'ing the request
		resp, err := rqst.Do()
		if err != nil {
			if e, ok := err.(*googleapi.Error); ok {
				fmt.Printf("Error code: %v\n", e.Code)
				fmt.Printf("Message: %v\n", e.Message)
			} else {
				fmt.Printf("Error: %v\n", err)
			}
			os.Exit(1)
		}
		for _, user := range resp.Users {
			fmt.Printf("User: %s (%s)\n", user.Name.FullName, user.PrimaryEmail)
		}
		// Until there aren't any more pages
		if resp.NextPageToken == "" {
			break
		}
		// Using the NextPageToken to page
		rqst.PageToken(resp.NextPageToken)
	}
}

// acquire connections somehow
srcConnection, _ := sql.Open("sqlite3", "/path/to/database.db")
dstConnection, _ := sql.Open("sqlite3", ":memory:")
// get reference to underlying driver connection object
src := srcConnection.(*sqlite3.SQLiteConn)
dst := dstConnection.(*sqlite3.SQLiteConn)
// start a backup operation
//
// see: https://www.sqlite.org/c3ref/backup_finish.html#sqlite3backupinit
// see: https://pkg.go.dev/github.com/mattn/go-sqlite3#SQLiteBackup
backup, _ := dst.Backup("main" /* name of the database */, src, "main")
defer backup.Finish()
// stepping copies the pages
//
// see: https://www.sqlite.org/c3ref/backup_finish.html#sqlite3backupstep
// see: https://pkg.go.dev/github.com/mattn/go-sqlite3#SQLiteBackup.Step
_, _ = backup.Step(-1 /* number of pages to copy */)
// serialize the database into a buffer
data, _ := dst.Serialize("main")
// copy the buffer into sink
_, _ = io.Copy(sink, bytes.NewReader(data)) // assuming sink is your final io.Writer

package main
import (
	"crypto/hmac"
	"crypto/sha256"
	"crypto/sha512"
	"encoding/base64"
	"log"
	"strconv"
	"time"
)
func createAPISign(apiSecret string) (string, string, error) {
	apiPath := "/0/private/GetWebSocketsToken"
	nonce := strconv.FormatInt(time.Now().UnixMilli(), 10)
	postData := "nonce=" + nonce
	concatenatedNonce := nonce + postData
	apiSha256 := sha256.New()
	// write the concatenated nonce to the sha256 object
	apiSha256.Write([]byte(concatenatedNonce))
	// retrieve bytes for what's been written to the sha256 object
	apiSha256Bytes := apiSha256.Sum(nil)
	// decode the api secret into bytes
	encodedSecret, err := base64.StdEncoding.DecodeString(apiSecret)
	if err != nil {
		return "", "", err
	}
	// create the hmac object
	h := hmac.New(sha512.New, encodedSecret)
	// write the api path and the sha256 bytes to the hmac object
	h.Write(append([]byte(apiPath), apiSha256Bytes...))
	// retrieve the hmac digest
	hmacDigest := h.Sum(nil)
	// encode the hmac digest into a base64 string
	apiSignature := base64.StdEncoding.EncodeToString(hmacDigest)
	return postData, apiSignature, nil
}
func main() {
	apiSecret := "apiSecret"
	postData, apiSignature, err := createAPISign(apiSecret)
	if err != nil {
		log.Fatalf("Error creating API sign: %v", err)
	}
	log.Printf("postData: %s\napiSignature: %s\n", postData, apiSignature)
}

