package main
import (
	"fmt"
	"regexp"
)
func main() {
	stringifiedHTML := `<h2>Here is John Doe</h2>
<div>Hi my name is Jane Bob.</div>
<p>Joe Smith is my name.</p>
<button>Jen Willis</button>`
	fmt.Println(betweenValidTags(stringifiedHTML, "John Doe"))   // true
	fmt.Println(betweenValidTags(stringifiedHTML, "Jane Bob"))   // false
	fmt.Println(betweenValidTags(stringifiedHTML, "Joe Smith"))  // true
	fmt.Println(betweenValidTags(stringifiedHTML, "Jen Willis")) // false
}
func betweenValidTags(input string, subStr string) bool {
	// regex for tags <p>, <li>, <h2>
	inTags := fmt.Sprintf(`<(p|li|h2)[^>]*>.*?%s.*?</(p|li|h2)>`, regexp.QuoteMeta(subStr))
	re := regexp.MustCompile(inTags)
	return re.MatchString(input)
}

package main
import (
	"fmt"
	"regexp"
	"strings"
)
func main() {
	stringifiedHTML := `<h2>Here is John Doe</h2>
<div>Hi my name is Jane Bob.</div>
<p>Joe Smith is my name.</p>
<button>Jen Willis</button>`
	fmt.Println(betweenValidTags(stringifiedHTML, "John Doe"))   // true
	fmt.Println(betweenValidTags(stringifiedHTML, "Jane Bob"))   // false
	fmt.Println(betweenValidTags(stringifiedHTML, "Joe Smith"))  // true
	fmt.Println(betweenValidTags(stringifiedHTML, "Jen Willis")) // false
}
var tagRegex = regexp.MustCompile(`<(p|li|h2)[^>]*>(.*?)</(p|li|h2)>`)
func betweenValidTags(input string, subStr string) bool {
	matches := tagRegex.FindAllStringSubmatch(input, -1)
	// Iterate through all matches and check if subStr is within the matched content
	for _, match := range matches {
		if len(match) > 2 && strings.Contains(match[2], subStr) {
			return true
		}
	}
	return false
}

package main
import (
	"encoding/xml"
	"fmt"
)
type RDF struct {
	Classes []Class `xml:"Class"`
}
type Class struct {
	ClassDefinition []ClassDescriptor `xml:"subClassOf"`
}
type ClassDescriptor struct {
	NodeID     string
	OnProperty string
}
func (description *ClassDescriptor) UnmarshalXML(d *xml.Decoder, start xml.StartElement) error {
	if len(start.Attr) > 0 {
		var v struct {
			NodeID string `xml:"nodeID,attr"`
		}
		if err := d.DecodeElement(&v, &start); err != nil {
			return fmt.Errorf("can't decode node id on %+v: %w", start, err)
		}
		description.NodeID = v.NodeID
	} else {
		var v struct {
			Restriction struct {
				OnProperty struct {
					Resource string `xml:"resource,attr"`
				} `xml:"onProperty"`
			} `xml:"Restriction"`
		}
		if err := d.DecodeElement(&v, &start); err != nil {
			return fmt.Errorf("can't decode node id on %+v: %w", start, err)
		}
		description.OnProperty = v.Restriction.OnProperty.Resource
	}
	return nil
}

