import (
	"encoding/json"
	"github.com/pulumi/pulumi-aws/sdk/v6/go/aws/s3"
	"github.com/pulumi/pulumi/sdk/v3/go/pulumi"
)
func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		bucket, err := s3.NewBucket(ctx, "bucket", nil)
		if err != nil {
			return err
		}
		accessPoint, err := s3.NewAccessPoint(ctx, "access-point", &s3.AccessPointArgs{
			Bucket: bucket.ID(),
		})
		if err != nil {
			return err
		}
		policy, err := s3.NewBucketPolicy(ctx, "policy", &s3.BucketPolicyArgs{
			Bucket: bucket.ID(),
			// ðŸ‘‡ Use .ApplyT() to wait for the ARN to be available, then compose a new string output with it.
			Policy: accessPoint.Arn.ApplyT(func(arn string) (pulumi.StringOutput, error) {
				p, err := json.Marshal(map[string]interface{}{
					"Version": "2012-10-17",
					"Statement": []map[string]interface{}{
						{
							"Effect": "Allow",
							"Action": "s3:ListBucket",
							"Principal": map[string]string{
								"AWS": "exampleValue",
							},
							// ðŸ‘‡ Use the unwrapped string.
							"Resource": arn + "/*",
						},
					},
				})
				if err != nil {
					return pulumi.StringOutput{}, err
				}
				// ðŸ‘‡ Return a new, transformed string output.
				return pulumi.String(p).ToStringOutput(), nil
			}).(pulumi.StringOutput),
		})
		if err != nil {
			return err
		}
		ctx.Export("policy", policy.ID())
		return nil
	})
}

