// resolver.go
func SendOtpEmail(db *gorm.DB, req *http.Request) *graphql.Field {
	return &graphql.Field{
		Type:        graphqlGlobal.ResultType,
		Description: "Send OTP to user's email.",
		Args: graphql.FieldConfigArgument{
			"email": &graphql.ArgumentConfig{Type: graphql.NewNonNull(graphql.String)},
		},
		Resolve: func(params graphql.ResolveParams) (interface{}, error) {
			return resolver(params, db, req)
		},
	}
}
type Args struct {
	Email string `json:"email" validate:"required,email"`
}
func resolver(params graphql.ResolveParams, db *gorm.DB, req *http.Request) (interface{}, error) {
	args := params.Args
	success := true
	messages := []string{}
	var argsDecoded Args
	err := mapstructure.Decode(args, &argsDecoded)
	if err != nil {
		success = false
		messages = []string{"Decoding map data type failed."}
	}
	if success {
		validate := validator.New()
		errValidate := validate.Struct(argsDecoded)
		if errValidate != nil {
			success = false
			messages = strings.Split(errValidate.Error(), "\n")
		}
	}
	if success {
		userImpl := repository.NewAuthRepo(db, req)
		email := argsDecoded.Email
		err := userImpl.SendOtpEmail(email)
		if err != nil {
			success = false
			messages = []string{err.Error()}
		}
	}
	response := fiber.Map{
		"success":  success,
		"messages": messages,
	}
	return response, nil
}

package main
import (
	"encoding/base64"
	"fmt"
)
func XOR(text string, encrypt bool, key string) string {
	// Convert the key to a byte array
	keyBytes := []byte(key)
	// If decrypting, decode the input text from base64
	if !encrypt {
		decodedText, err := base64.StdEncoding.DecodeString(text)
		if err != nil {
			fmt.Println("Error decoding base64:", err)
			return ""
		}
		text = string(decodedText)
	}
	// Convert the text to a byte array
	textBytes := []byte(text)
	// Initialize result array
	resultBytes := make([]byte, len(textBytes))
	// Perform XOR operation
	for i := 0; i < len(textBytes); i++ {
		// Get the corresponding byte from the key, using modulo to repeat the key if necessary
		keyByte := keyBytes[i%len(keyBytes)]
		// Perform XOR
		if encrypt {
			resultBytes[i] = textBytes[i] ^ keyByte
		} else {
			// For decryption, XOR operation is the same as encryption
			resultBytes[i] = textBytes[i] ^ keyByte
		}
	}
	// If encrypting, encode the result as base64
	if encrypt {
		resultText := base64.StdEncoding.EncodeToString(resultBytes)
		return resultText
	}
	// If decrypting, return the result as a string
	return string(resultBytes)
}

// This example demonstrates verifying a secp256k1 signature against a public
// key that is first parsed from raw bytes.  The signature is also parsed from
// raw bytes.
func Example_verifySignature() {
	// Decode hex-encoded serialized public key.
	pubKeyBytes, err := hex.DecodeString("02a673638cb9587cb68ea08dbef685c" +
		"6f2d2a751a8b3c6f2a7e9a4999e6e4bfaf5")
	if err != nil {
		fmt.Println(err)
		return
	}
	pubKey, err := btcec.ParsePubKey(pubKeyBytes)
	if err != nil {
		fmt.Println(err)
		return
	}
	// Decode hex-encoded serialized signature.
	sigBytes, err := hex.DecodeString("30450220090ebfb3690a0ff115bb1b38b" +
		"8b323a667b7653454f1bccb06d4bbdca42c2079022100ec95778b51e707" +
		"1cb1205f8bde9af6592fc978b0452dafe599481c46d6b2e479")
	if err != nil {
		fmt.Println(err)
		return
	}
	signature, err := ecdsa.ParseSignature(sigBytes)
	if err != nil {
		fmt.Println(err)
		return
	}
	// Verify the signature for the message using the public key.
	message := "test message"
	messageHash := chainhash.DoubleHashB([]byte(message))
	verified := signature.Verify(messageHash, pubKey)
	fmt.Println("Signature Verified?", verified)
	// Output:
	// Signature Verified? true
}

func keyIDFromCryptoKey(pubKey crypto.PublicKey) string {
	// Generate and return a fingerprint of the public key.
	// For an RSA key this should be:
	//   SHA256(DER encoded ASN1)
	// Then truncated to 240 bits and encoded into 12 base32 groups like so:
	//   ABCD:EFGH:IJKL:MNOP:QRST:UVWX:YZ23:4567:ABCD:EFGH:IJKL:MNOP
	derBytes, err := x509.MarshalPKIXPublicKey(pubKey)
	if err != nil {
		return ""
	}
	hasher := crypto.SHA256.New()
	hasher.Write(derBytes)
	return keyIDEncode(hasher.Sum(nil)[:30])
}

