func main() {
	brokers := []string{"localhost:9092", "localhost:9093"}
	cfg := sarama.NewConfig()
	cfg.Producer.Return.Successes = true
	producer, err := sarama.NewSyncProducer(brokers, cfg)
	if err != nil {
		log.Fatal(err)
	}
	counter := 0
	for {
		msg := sarama.ProducerMessage{
			Topic:     topic,
			Partition: -1,
			Value:     sarama.StringEncoder(fmt.Sprintf("Hello world %d", counter)),
		}
		if counter%2 == 0 {
			msg.Key = sarama.StringEncoder("two")
		} else {
			msg.Key = sarama.StringEncoder("one")
		}
		partition, offset, err := producer.SendMessage(&msg)
		if err != nil {
			log.Print("Failed to sent message", err)
		}
		log.Printf("Sent message to partiotion %d with offset %d key %s", partition, offset, msg.Key)
		counter++
		time.Sleep(10 * time.Millisecond)
	}
}

func (h *HTTP) renderError(w http.ResponseWriter, r *http.Request, err error) {
            switch e := err.(type) {
            case *app.AppError:
                w.WriteHeader(e.Status)
                render.JSON(w, r, e)
                return
            default:
                internalErr := app.NewInternalError("internal-error", "Something went wrong", nil)
                w.WriteHeader(internalErr.Status)
                render.JSON(w, r, internalErr)
                return
            }
}

func merge_sort(array []uint) []uint {
	if len(array) > 1 {
		r := len(array) / 2
		L := array[:r]
		M := array[r:]
		L = merge_sort(L)
		M = merge_sort(M)
		result := make([]uint, len(array))
		i, j, k := 0, 0, 0
		for i < len(L) && j < len(M) {
			if L[i] <= M[j] {
				result[k] = L[i]
				i++
			} else {
				result[k] = M[j]
				j++
			}
			k++
		}
		for i < len(L) {
			result[k] = L[i]
			i++
			k++
		}
		for j < len(M) {
			result[k] = M[j]
			j++
			k++
		}
		return result
	}
	return array
}

