func ImageNewFromSvg(svg string) (*gtk.Image, error){
  pixbuf, err := gdk.PixbufNewFromDataOnly([]byte(svg))
  if err !=  nil {
  	return nil, err
  }

  img, err := gtk.ImageNewFromPixbuf(pixbuf)

  if err !=  nil {
  	return nil, err
  }

  return img, nil
}

