type UserGroups map[string][]string
func main() {
	file, err := os.Open("/etc/passwd")
	if err != nil {
		panic(err)
	}
	defer file.Close()
	scanner := bufio.NewScanner(file)
	userGroups := make(UserGroups)
	for scanner.Scan() {
		fields := strings.Split(scanner.Text(), ":")
		username := fields[0]
		cmd := exec.Command("id", "-Gn", username)
		output, err := cmd.Output()
		if err != nil {
			panic(err)
		}
		groups := strings.Split(strings.TrimSpace(string(output)), " ")
		userGroups[username] = groups
	}
	for username, groups := range userGroups {
		fmt.Printf("%s: %s\n", username, strings.Join(groups, ", "))
	}
}

// https://learn.microsoft.com/en-us/windows/win32/procthread/process-security-and-access-rights
const PROCESS_ALL_ACCESS = windows.STANDARD_RIGHTS_REQUIRED | windows.SYNCHRONIZE | 0xffff
func SetPriorityWindows(pid int, priority uint32) error {
	handle, err := windows.OpenProcess(PROCESS_ALL_ACCESS, false, uint32(pid))
	if err != nil {
		return err
	}
    defer windows.CloseHandle(handle) // Technically this can fail, but we ignore it if it does
	err = windows.SetPriorityClass(handle, priority)
	if err != nil {
		return err
	}
}

