import (
	"log"
	"github.com/gofiber/fiber/v2"
	"github.com/gofiber/websocket/v2"
)
var (
	register   = make(chan *websocket.Conn)
	unregister = make(chan *websocket.Conn)
)
func main() {
	// Make it easy to find out which line prints the log.
	log.SetFlags(log.Lshortfile)
	app := fiber.New()
	app.Get("/ws/monitor", websocket.New(HandleWsMonitor))
	log.Fatal(app.Listen(":8080"))
}
func HandleWsMonitor(c *websocket.Conn) {
	// It seems the we only need one SocketListener goroutine for the whole server.
	// If this is the case, the next line should be moved outside of this func.
	go SocketListener()
	defer func() {
		unregister <- c
		c.Close()
	}()
	register <- c
	for {
		messageType, message, err := c.ReadMessage()
		if err != nil {
			if websocket.IsUnexpectedCloseError(err, websocket.CloseGoingAway, websocket.CloseAbnormalClosure) {
				log.Println("read error:", err)
			}
			return
		}
		if messageType == websocket.TextMessage {
			log.Println("got textmessage:", string(message))
		} else {
			log.Println("received message of type:", messageType)
		}
	}
}
func SocketListener() {
	for {
		select {
		case c := <-register:
			// This did appear in the log.
			log.Println("case c := <-register")
			messageType, message, err := c.ReadMessage()
			if err != nil {
				log.Println(err)
				// unregister is unbuffered, the sending will be blocked.
				unregister <- c
				// If we use only one SocketListener goroutine then it should
				// not return here.
				return
			}
			log.Printf("Got message of type: %d\nMessage:%s\n", messageType, string(message))
			log.Printf("Connection Params: %s\n", c.Params("id"))
		case c := <-unregister:
			c.Close()
			log.Println("Closed connection")
		}
	}
}

package queue
import (
	"sync/atomic"
)
// LockfreeQueue represents a FIFO structure with operations to enqueue
// and dequeue generic values.
// Reference: https://www.cs.rochester.edu/research/synchronization/pseudocode/queues.html
type LockFreeQueue[T any] struct {
	head atomic.Pointer[node[T]]
	tail atomic.Pointer[node[T]]
}
// node represents a node in the queue
type node[T any] struct {
	value T
	next  atomic.Pointer[node[T]]
}
// newNode creates and initializes a node
func newNode[T any](v T) *node[T] {
	return &node[T]{value: v}
}
// NewQueue creates and initializes a LockFreeQueue
func NewLockFreeQueue[T any]() *LockFreeQueue[T] {
	var head atomic.Pointer[node[T]]
	var tail atomic.Pointer[node[T]]
	var n = node[T]{}
	head.Store(&n)
	tail.Store(&n)
	return &LockFreeQueue[T]{
		head: head,
		tail: tail,
	}
}
// Enqueue adds a series of Request to the queue
func (q *LockFreeQueue[T]) Enqueue(v T) {
	n := newNode(v)
	for {
		tail := q.tail.Load()
		next := tail.next.Load()
		if tail == q.tail.Load() {
			if next == nil {
				if tail.next.CompareAndSwap(next, n) {
					q.tail.CompareAndSwap(tail, n)
					return
				}
			} else {
				q.tail.CompareAndSwap(tail, next)
			}
		}
	}
}
// Dequeue removes a Request from the queue
func (q *LockFreeQueue[T]) Dequeue() T {
	var t T
	for {
		head := q.head.Load()
		tail := q.tail.Load()
		next := head.next.Load()
		if head == q.head.Load() {
			if head == tail {
				if next == nil {
					return t
				}
				q.tail.CompareAndSwap(tail, next)
			} else {
				v := next.value
				if q.head.CompareAndSwap(head, next) {
					return v
				}
			}
		}
	}
}
// Check if the queue is empty.
func (q *LockFreeQueue[T]) IsEmpty() bool {
	return q.head.Load() == q.tail.Load()
}

