	"github.com/farrellit/writeprogress"
)
func main() {
	in, err := os.Open("/dev/urandom")
	if err != nil {
		panic(err)
	}
	out, err := os.OpenFile("/dev/null", os.O_WRONLY, 0)
	if err != nil {
		panic(err)
	}
	defer in.Close()
	defer out.Close()
	length := int64(1e6)
	wp := writeprogress.NewProgressWriter(uint64(length))
	d, _ := wp.Watch(func(p float64) { fmt.Printf("\r%2.0f%%", p*100) })
	if b, err := io.Copy(
		io.MultiWriter(out, wp),
		&io.LimitedReader{R: in, N: length},
	); err != nil {
		panic(err)
	} else {
		<-d
		fmt.Printf("\n%d/%d %2.0f%%\n", b, length, wp.GetProgress()*100)
	}
}

package main
import (
	"fmt"
	"strings"
	"sort"
)
func main() {
	a := map[string]string{
	"Key1": strings.Repeat("Value1", 50),
	"Key2": strings.Repeat("Value2", 50),
	"Key3": strings.Repeat("Value3", 50),
	}

	columner(a, 30)
}
func columner(m map[string]string, charAmount int) {
	var keys []string

	var keyLens []int

	// to avoid index panics and gathering keys for later usage
	for key, value := range m {
		if charAmount > len(value) || charAmount < 1{
			value += strings.Repeat(" ", charAmount-len(value))
			m[key] = value
		}
		keys = append(keys, key)
		keyLens = append(keyLens, len(key))
	}
	sort.Ints(keyLens)

	for i := 0; i < len(keys); i++ {

		// for storing updated value of key
		var value2 string

		value := m[keys[i]]
		// will used while extracting substring of key's value as first index
		firstI := 0

		// last index for extract substring from key's value. the len of substring will be same as charAmount
		charAmount2 := charAmount

		// will be used to advance next substring of key's value
		advance := charAmount2

		// spaces between between key and value
		// key       value
		spacing := strings.Repeat(" ", 20 + (keyLens[0] - len(keys[i])))

		// var for adjusting spaces of gap between key and value of next line
		// key        value
		//          value
		// to
		// key        value
		//            value
		spacingU := spacing + strings.Repeat(" ", len(keys[i]) + 1)

		// this loop will be run as long as there is no substring left which exceed next line
		for j := 0; j < len(value); j += advance {

			// adjusting spaces of gap between key and value of next line
			if j > 0 {
				spacing = spacingU
			}

			// add space between key and value, then extract substring, then add spaces to the next line of the
			// next substring of key's value
			value2 += spacing + value[firstI:charAmount2] + "\n"

			// finish loop when there is no substring that can be exceed to next line
			if ((len(value) - charAmount2) < advance) || ((len(value) - charAmount2) == advance) {
				break
			}

			// changing first index to start index of next substring of key's value
			firstI = charAmount2

			// advancing to next substring of key's value
			charAmount2 += advance
		}

		// add last remaining substring of key's value to variable which will be show as formatted.
		value2 += spacing + value[charAmount2:]
		// show formatted key and value
		fmt.Println(keys[i], value2, "\n")

	}
}

package main
import (
	"runtime"
	"sync"
	"sync/atomic"
)
type ErrorNotifier interface {
	SetEmailSenderService(func(string))
	SendErrorMessage(string)
}
// Mutex
type MutexErrorNotifier struct {
	mutex       sync.Mutex
	emailSender func(string)
}
var _ ErrorNotifier = (*MutexErrorNotifier)(nil)
func (a *MutexErrorNotifier) SetEmailSenderService(emailSender func(string)) {
	a.mutex.Lock()
	defer a.mutex.Unlock()
	a.emailSender = emailSender
}
func (a *MutexErrorNotifier) SendErrorMessage(errorMessage string) {
	a.mutex.Lock()
	defer a.mutex.Unlock()
	if a.emailSender != nil {
		a.emailSender(errorMessage)
	}
}
// RWMutex
type RWMutexErrorNotifier struct {
	rwMutex     sync.RWMutex
	emailSender func(string)
}
var _ ErrorNotifier = (*RWMutexErrorNotifier)(nil)
func (a *RWMutexErrorNotifier) SetEmailSenderService(emailSender func(string)) {
	a.rwMutex.Lock()
	defer a.rwMutex.Unlock()
	a.emailSender = emailSender
}
func (a *RWMutexErrorNotifier) SendErrorMessage(errorMessage string) {
	a.rwMutex.RLock()
	defer a.rwMutex.RUnlock()
	if a.emailSender != nil {
		a.emailSender(errorMessage)
	}
}
// Atomic Boolean
type AtomicBooleanErrorNotifier struct {
	emailerIsSet atomic.Bool
	emailSender  func(string)
}
var _ ErrorNotifier = (*AtomicBooleanErrorNotifier)(nil)
func (a *AtomicBooleanErrorNotifier) SetEmailSenderService(emailSender func(string)) {
	defer a.emailerIsSet.Store(true)
	a.emailSender = emailSender
}
func (a *AtomicBooleanErrorNotifier) SendErrorMessage(errorMessage string) {
	if a.emailerIsSet.Load() {
		a.emailSender(errorMessage)
	}
}
// NOT A SOLUTION: racy no locking solution - just for benchmarking
type RacyNoGoodErrorNotifier struct {
	emailSender func(string)
}
var _ ErrorNotifier = (*RacyNoGoodErrorNotifier)(nil)
func (a *RacyNoGoodErrorNotifier) SetEmailSenderService(emailSender func(string)) {
	a.emailSender = emailSender
}
func (a *RacyNoGoodErrorNotifier) SendErrorMessage(errorMessage string) {
	if a.emailSender != nil {
		a.emailSender(errorMessage)
	}
}
// Demo run
const allConcurrent = "all concurrent"
const sequentialSingleCore = "sequential single core"
const sequentialAllCores = "sequential all cores"
func Run(n int, runner ErrorNotifier, runType string) {
	emailSender := func(emailMessage string) {
		// sending email...
	}
	var wg sync.WaitGroup
	switch runType {
	case allConcurrent:
		wg.Add(n * runtime.NumCPU())
		for i := 0; i < n*runtime.NumCPU(); i++ {
			go func() {
				runner.SendErrorMessage("ALARM!")
				wg.Done()
			}()
		}
	case sequentialAllCores:
		wg.Add(runtime.NumCPU())
		for i := 0; i < runtime.NumCPU(); i++ {
			go func() {
				for j := 0; j < n; j++ {
					runner.SendErrorMessage("ALARM!")
				}
				wg.Done()
			}()
		}
	case sequentialSingleCore:
		wg.Add(1)
		go func() {
			for j := 0; j < n*runtime.NumCPU(); j++ {
				runner.SendErrorMessage("ALARM!")
			}
			wg.Done()
		}()
	default:
		panic("unknown mode")
	}
	runner.SetEmailSenderService(emailSender)
	wg.Wait()
}

