import (
	"bufio"
	"fmt"
	"io"
	"strconv"
	"strings"
)
func getInput(reader *bufio.Reader) (string, error) {
	data, err := reader.ReadString('\n')
	if err != nil {
		return "", fmt.Errorf("get input error: %w", err)
	}
	return strings.ReplaceAll(data, "\n", ""), nil
}
type Person struct {
	Name string
	Age  int
}
func NestedInput(input io.Reader) (*Person, error) {
	reader := bufio.NewReader(input)
	name, err := getInput(reader)
	if err != nil {
		return nil, err
	}
	switch name {
	case "q":
		return nil, nil
	default:
		ageStr, err := getInput(reader)
		if err != nil {
			return nil, err
		}
		age, err := strconv.Atoi(ageStr)
		if err != nil {
			return nil, err
		}
		return &Person{Name: name, Age: age}, nil
	}
}

func (p *Product) UnmarshalJSON(b []byte) error {
	type product Product // revent recursion
	var _p product
	if err := json.Unmarshal(b, &_p); err != nil {
		return err
	}
	*p = Product(_p)
	return unmarshalFieldsWithSingleQuotes(p, b)
}
func unmarshalFieldsWithSingleQuotes(dest interface{}, b []byte) error {
	// Look through the JSON tags. If there is one containing single quotes,
	// unmarshal b again, into a map this time. Then unmarshal the value
	// at the map key corresponding to the tag, if any.
	var m map[string]json.RawMessage
	t := reflect.TypeOf(dest).Elem()
	v := reflect.ValueOf(dest).Elem()
	for i := 0; i < t.NumField(); i++ {
		tag := t.Field(i).Tag.Get("json")
		if !strings.Contains(tag, "'") {
			continue
		}
		if m == nil {
			if err := json.Unmarshal(b, &m); err != nil {
				return err
			}
		}
		if j, ok := m[tag]; ok {
			if err := json.Unmarshal(j, v.Field(i).Addr().Interface()); err != nil {
				return err
			}
		}
	}
	return nil
}

