func (nc *NodeConfig) UnmarshalYAML(value *yaml.Node) error {
	var ncu struct {
		Type   string    `yaml:"type"`
		Config yaml.Node `yaml:"config"`
	}
	var err error
	// unmarshall into a NodeConfigUnmarshaler to detect correct type
	err = value.Decode(&ncu)
	if err != nil {
		return err
	}
	// now, detect the type and covert it accordingly
	nc.Type = ncu.Type
	switch ncu.Type {
	case "request":
		nc.Config = &RequestConfig{}
	case "log":
		nc.Config = &LogConfig{}
	default:
		return fmt.Errorf("unknown type %q", ncu.Type)
	}
	err = ncu.Config.Decode(nc.Config)
	return err
}

type RequestConfig struct {
	Foo string `yaml:"foo"`
	Bar string `yaml:"bar"`
}
type LogConfig struct {
	Message string `yaml:"message"`
}
func main() {
	logSampleYAML := []byte(`
type: log
config:
    message: this is a log message
`)
	reqSampleYAML := []byte(`
type: request
config:
    foo: foo value
    bar: bar value
`)
	for i, val := range [][]byte{logSampleYAML, reqSampleYAML} {
		var nc NodeConfig
		err := yaml.Unmarshal(val, &nc)
		if err != nil {
			fmt.Printf("failed to parse sample %d: %v\n", i, err)
		} else {
			fmt.Printf("sample %d type %q (%T) = %+v\n", i, nc.Type, nc.Config, nc.Config)
		}
	}
}

func (nc *NodeConfig) UnmarshalYAML(unmarshal func(interface{}) error) error {
	var ncu struct {
		Type   string     `yaml:"type"`
		Config RawMessage `yaml:"config"`
	}
	var err error
	// unmarshall into a NodeConfigUnmarshaler to detect correct type
	err = unmarshal(&ncu)
	if err != nil {
		return err
	}
	// now, detect the type and covert it accordingly
	nc.Type = ncu.Type
	switch ncu.Type {
	case "request":
		cfg := &RequestConfig{}
		err = ncu.Config.Unmarshal(cfg)
		nc.Config = cfg
	case "log":
		cfg := &LogConfig{}
		err = ncu.Config.Unmarshal(cfg)
		nc.Config = cfg
	default:
		return fmt.Errorf("unknown type %q", ncu.Type)
	}
	return err
}

type UserItem struct {
	User   string `json:"user"`
	Status string `json:"status"`
}
type UserList []UserItem
func (ul *UserList) UnmarshalJSON(data []byte) error {
	switch {
	case len(data) == 0 || string(data) == `null`:
		return nil
	case data[0] == '[': // assume it's a JSON array
		return json.Unmarshal(data, (*[]UserItem)(ul))
	case data[0] == '{': // assume it's a JSON object
		obj := make(map[string]UserItem)
		if err := json.Unmarshal(data, &obj); err != nil {
			return err
		}
		for _, v := range obj {
			*ul = append(*ul, v)
		}
		return nil
	default:
		return fmt.Errorf("unsupported json type")
	}
	return nil
}

