type Root struct {
	One Nested
	Two Nested
}
type Nested struct {
	i int
	s string
}
func main() {
	r := Root{
		One: Nested{i: 1, s: "one"},
		Two: Nested{i: 2, s: "two"},
	}
	rv := reflect.Indirect(reflect.ValueOf(&r))
	rt := rv.Type()
	for i, limit := 0, rt.NumField(); i < limit; i++ {
		fld := rt.Field(i)
		v := rv.FieldByName(fld.Name).Addr().Interface()
		nv, ok := v.(*Nested)
		if !ok {
			log.Fatalf("field is type %T not Nested", v)
		}
		log.Printf("field name: %s, type: %T, i: %v, s: %v", fld.Name, v, nv.i, nv.s)
	}
}

