// Paginate is a Gorm scope function.
func Paginate(entity BaseEntity, p *Pagination) func(db *gorm.DB) *gorm.DB {
	return func(db *gorm.DB) *gorm.DB {
		var totalRows int64
		// we must create a new session to run the count, otherwise by using the same db connection
		// we'll get some residual data which will cause db.Offset(p.Offset).Limit(p.PerPage) to fail.
		countDBSession := db.Session(&gorm.Session{Initialized: true})
		countDBSession.Model(entity).Count(&totalRows)
		totalPages := int(math.Ceil(float64(totalRows) / float64(p.PerPage)))
		p.TotalPages = totalPages
		p.TotalCount = int(totalRows)
		p.SetLinks(entity.ResourceName())
		return db.Offset(p.Offset).Limit(p.PerPage)
	}
}

func (c *cycleDet) hasCycle() bool {
    ctx, cancel := context.WithCancel(context.Background())
    hasCycle := make(chan bool)
    var wg sync.WaitGroup
    adj := c.b // adjacency list representation of the unerlying graph
    for node := range adj {
        wg.Add(1)
        go func(ctx context.Context, no nodeID, co chan<- bool, cancel context.CancelFunc) {
            // Use wg to synchronize termination of read-only goroutines.
            defer wg.Done()
            select {
                case <-ctx.Done():
                    return
                default:
            }
            visited := make(map[nodeID]struct{})
            // c.nodeHasCycle will use a recursive implementation of DFS to
            // find out if the node no leads to a cycle.
            if c.nodeHasCycle(ctx, no, visited) {
                co <- true
                cancel()
                return
            }
        }(ctx, node, hasCycle, cancel)
    }
    // Observer goroutine to notify when wg is done waiting.
    time.Sleep(100 * time.Millisecond)
    wg.Wait()
    defer cancel()
    select {
    case <-hasCycle:
        fmt.Println("got a cycle")
        return true
    default:
        fmt.Println("no cycle detected")
        return false
    }
}

