package main
import (
	"encoding/json"
	"fmt"
)
type Input struct {
	CustomerId string `json:"customer_id"`
}
type InputList []Input
type Output struct {
	CustomerIds []string `json:"Customer_id"`
}
func main() {
	jsonSource := `[
        { "customer_id": "g62" },
        { "customer_id": "b23" },
        { "customer_id": "a34" },
        { "customer_id": "c42" }
    ]`
	input := deserialize(jsonSource)
	fmt.Println()
	fmt.Println("Input:")
	fmt.Printf("%+v\n", input)
	output := transform(input)
	fmt.Println()
	fmt.Println("Output:")
	fmt.Printf("%+v\n", output)
	jsonFinal := serialize(output)
	fmt.Println()
	fmt.Println("Final JSON:")
	fmt.Println(jsonFinal)
}
func deserialize(s string) InputList {
	var input InputList
	if err := json.Unmarshal([]byte(s), &input); err != nil {
		panic(err)
	}
	return input
}
func transform(input InputList) Output {
	output := Output{
		CustomerIds: make([]string, 0, len(input)),
	}
	for _, item := range input {
		output.CustomerIds = append(output.CustomerIds, item.CustomerId)
	}
	return output
}
func serialize(output Output) string {
	buf, err := json.Marshal(output)
	if err != nil {
		panic(err)
	}
	s := string(buf)
	return s
}

func (b *Buckets) UnmarshalXML(d *xml.Decoder, start xml.StartElement) error {
	b.XMLName = start.Name
	var currentBucket *Bucket
	var currentItem *Item
	for {
		t, err := d.Token()
		if t == nil {
			// append the last bucket before exiting
			b.Buckets = append(b.Buckets, currentBucket)
			break
		}
		if err != nil {
			return err
		}
		switch se := t.(type) {
		case xml.StartElement:
			switch se.Name.Local {
			case "Bucket-name":
				// check if currentBucket is nil, it is necessary for the first time that
				// is going to run. Otherwise, append the last bucket to the slice and reset it
				if currentBucket != nil {
					b.Buckets = append(b.Buckets, currentBucket)
				}
				currentBucket = &Bucket{}
				if err := d.DecodeElement(&currentBucket.BucketName, &se); err != nil {
					return err
				}
			case "item-name":
				currentItem = &Item{}
				if err := d.DecodeElement(&currentItem.Name, &se); err != nil {
					return err
				}
			case "weight":
				if err := d.DecodeElement(&currentItem.Weight, &se); err != nil {
					return err
				}
			case "quantity":
				if err := d.DecodeElement(&currentItem.Quantity, &se); err != nil {
					return err
				}
				// since quantity comes last append the item to the bucket,  and reset it
				currentBucket.Items = append(currentBucket.Items, currentItem)
				currentItem = &Item{}
			}
		}
	}
	return nil
}

