func setNonblock(f *os.File) error {
	c, err := f.SyscallConn()
	if err != nil {
		return err
	}
	var err2 error
	err = c.Control(func(fd uintptr) {
		err2 = syscall.SetNonblock(int(fd), true)
	})
	if err != nil {
		return err
	}
	return err2
}
func nonBlockingFile(f *os.File) (*os.File, error) {
	if err := setNonblock(f); err != nil {
		return nil, err
	}
	fd, err := syscall.Dup(int(f.Fd()))
	if err != nil {
		return nil, err
	}
	f2 := os.NewFile(uintptr(fd), f.Name())
	return f2, nil
}
func read(ctx context.Context, f *os.File) (io.Reader, error) {
	r, err := nonBlockingFile(f)
	if err != nil {
		return nil, err
	}
	go func() {
		defer r.Close()
		<-ctx.Done()
	}()
	buff := bytes.NewBuffer([]byte{})
	for {
		_, err := io.Copy(buff, r)
		if err != nil {
			if errors.Is(err, fs.ErrClosed) {
				break
			}
			panic(err)
		}
	}
	return buff, nil
}
