import (
	"context"
	"fmt"
	"log"
	"sync"
)
func read(ctx context.Context) (<-chan int, <-chan error) {
	ch := make(chan int)
	e := make(chan error)
	go func() {
		defer close(e)
		defer close(ch)
		for i := 0; i < 12; i++ {
			select {
			case <-ctx.Done():
				return
			case ch <- i:
			}
		}
	}()
	return ch, e
}
func manipulate(in <-chan int) (<-chan int, <-chan error) {
	ch := make(chan int)
	e := make(chan error)
	go func() {
		defer close(e)
		defer close(ch)
		for n := range in {
			ch <- 2 * n
		}
	}()
	return ch, e
}
func write(in <-chan int) <-chan error {
	e := make(chan error)
	go func() {
		defer close(e)
		for n := range in {
			fmt.Println("written: ", n)
			if n == 10 {
				e <- fmt.Errorf("output error during write")
			}
		}
	}()
	return e
}
func collectErrors(errs ...<-chan error) {
	var wg sync.WaitGroup
	for i := 0; i < len(errs); i++ {
		wg.Add(1)
		go func(errs <-chan error) {
			defer wg.Done()
			for err := range errs {
				log.Printf("%v", err)
			}
		}(errs[i])
	}
	wg.Wait()
}
func main() {
	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()
	ch1, err1 := read(ctx)
	ch2, err2 := manipulate(ch1)
	err3 := write(ch2)
	collectErrors(err1, err2, err3)
	fmt.Println("main wait complete")
}

