func (n *Node) InsertNode(path string, o *Node) bool {
	parts := strings.Split(path, " ")
	target := n
	for _, part := range parts {
		found := false
		for _, child := range target.Children {
			if child.Name == part {
				target = child
				found = true
				break
			}
		}
		if !found {
			return false
		}
	}
	target.Children = append(target.Children, o)
	return true
}
