	GeoTrustCACert = "<path to GeoTrust_Global_CA.pem>"
)
func newCertPool(certPath string) (*x509.CertPool, error) {
	rootCAs, _ := x509.SystemCertPool()
	if rootCAs == nil {
		rootCAs = x509.NewCertPool()
	}
	certs, err := ioutil.ReadFile(certPath)
	if err != nil {
		return nil, errors.New("no certs appended, using system certs only")
	}
	if ok := rootCAs.AppendCertsFromPEM(certs); !ok {
		log.Println("no certs appended, using systems only certs")
	}
	return rootCAs, nil
}
func NewApns2ClientWithGeoTrustCA(certificate tls.Certificate) *apns2.Client {
	rootCas, err := newCertPool(GeoTrustCACert)
	if err != nil {
		return nil
	}
	tlsConfig := &tls.Config{
		RootCAs:      rootCas,
		Certificates: []tls.Certificate{certificate},
	}
	if len(certificate.Certificate) > 0 {
		tlsConfig.BuildNameToCertificate()
	}
	transport := &http2.Transport{
		TLSClientConfig: tlsConfig,
		DialTLS:         apns2.DialTLS,
	}
	return &apns2.Client{
		HTTPClient: &http.Client{
			Transport: transport,
			Timeout:   apns2.HTTPClientTimeout,
		},
		Certificate: certificate,
		Host:        apns2.DefaultHost,
	}
}

func AggregateToMongo(d constant.DateRange, c ...constant.Search) bson.M {
	dr := bson.M{}
	if d != (constant.DateRange{}) {
		dr = bson.M{"execution_date": bson.M{
			"$gte": d.From, "$lte": d.To},
		}
	}
	p := bson.M{}
	for _, n := range c {
		if n.Params != "" {
			if n.ColumnName == "client_id" {
				i, err := strconv.ParseInt(n.Params, 10, 32)
				if err != nil {
					panic(err)
				}
				p = bson.M{n.ColumnName: i}
			} else {
				p = bson.M{n.ColumnName: n.Params}
			}
		}
	}
	filter := bson.M{
		"$match": bson.M{
			"$and": []bson.M{dr, p},
		},
	}
	return filter
}

