package main
import (
	"fmt"
	"log"
	"os"
	"text/scanner"
	"time"
)
func main(){
	var file [2]*os.File
	var err error
	file[0], err = os.Open("./abc.txt")
	file[1], err = os.Open("./abc.txt")
	if err != nil {
		log.Panic(err)
	}
	defer file[0].Close()
	defer file[1].Close()
	var scanner [2]scanner.Scanner
	inChan := make(chan string)
	for i := 0; i < 2; i++ {
		var n int64 = (int64)(i) * 26
		file[i].Seek(n, 0)
		scanner[i].Init(file[i])
		fmt.Println(scanner[0].Pos)
		go parseLine(fmt.Sprintf("Scanner %v:", i), &scanner[i], inChan)
	}
	for msg := range inChan {
		fmt.Println(msg)
	}
}
func parseLine(name string, scanner *scanner.Scanner, out chan string) {
	for i := 0; i < 26; i++ {
		out <- fmt.Sprintf("%s %c", name, scanner.Next())
	}
	time.Sleep(time.Second * 10)
	close(out)
}

func getRootPath() string {
	dir, err := os.Getwd()
	if err != nil {
		log.Fatal(err)
	}
	// Check if already in root directory (e.g. main.go)
	lastSlashIndex := strings.LastIndex(dir, "/")
	cName := dir[lastSlashIndex+1:]
	if cName == "#root-folder-name#" {
		return dir
	}
	// Get parent directory
	parent := filepath.Dir(dir)
	lastSlashIndex = strings.LastIndex(parent, "/")
	pName := parent[lastSlashIndex+1:]
	// If not at root, continue getting parent
	for pName != "#root-folder-name#" {
		parent = filepath.Dir(parent)
		lastSlashIndex = strings.LastIndex(parent, "/")
		pName = parent[lastSlashIndex+1:]
	}
	return parent
}

// Storer takes the source and stores its contents under path for further reading via
// Retriever.
type Storer interface {
	StreamTo(path string, source io.Reader) (err error)
}
// Retriever takes a path and streams the file it has stored under path to w.
type Retriever interface {
	StreamFrom(path string, w io.Writer) (err error)
}
// Repository is a composite interface. It requires a
// repository to accept andf provide streams of files
type Repository interface {
	Storer
	Retriever
	Close() error
}

