package main
import (
	"crypto/aes"
	"crypto/cipher"
	"crypto/subtle"
	"encoding/hex"
	"fmt"
)
func main() {
	// Derive counter
	key := []byte("examplekey123456")
	nonce := []byte("1234567890abcdef")
	aesCipher, _ := aes.NewCipher(key)
	aesgcm, _ := GhashInit(aesCipher)
	counter := aesgcm.GhashDeriveCounter(nonce)
	fmt.Println("Initial counter (reserved for tag), hex: " + hex.EncodeToString(counter[:])) // reserved for tag
	// Derive tag (optional)
	ciphertext, _ := hex.DecodeString("710869a3f0dd6e579e9797f7a4f3cb79dc757d0a8a5ba3190d778f44db5ff7b28e665593b61ad1b14fff65")
	aad := []byte("some additional authenticated data")
	tag := aesgcm.GetAuthenticationTag(ciphertext, aad, counter[:])
	fmt.Println("Tag, hex: " + hex.EncodeToString(tag))
	// Increment tag fÃ¼r decryption
	counterInc(&counter)
	fmt.Println("Incremented counter (applied for encryption), hex: " + hex.EncodeToString(counter[:])) // applied for encryption
	// Decrypt with ctr
	block, _ := aes.NewCipher(key)
	plaintext := make([]byte, len(ciphertext))
	stream := cipher.NewCTR(block, counter[:])
	stream.XORKeyStream(plaintext, ciphertext)
	fmt.Println("Decrypted data using CTR: " + string(plaintext))
}
const (
	gcmBlockSize         = 16
	gcmStandardNonceSize = 12
	gcmTagSize           = 16
)
func GhashInit(cipher Block) (*gcm, error) {
	var key [gcmBlockSize]byte
	cipher.Encrypt(key[:], key[:])
	g := &gcm{cipher: cipher}
	x := gcmFieldElement{
		BeUint64(key[:8]),
		BeUint64(key[8:]),
	}
	g.productTable[reverseBits(1)] = x
	for i := 2; i < 16; i += 2 {
		g.productTable[reverseBits(i)] = gcmDouble(&g.productTable[reverseBits(i/2)])
		g.productTable[reverseBits(i+1)] = gcmAdd(&g.productTable[reverseBits(i)], &x)
	}
	return g, nil
}
func (g *gcm) GhashDeriveCounter(nonce []byte) [16]byte {
	var counter [gcmBlockSize]byte
	if len(nonce) == gcmStandardNonceSize {
		copy(counter[:], nonce)
		counter[gcmBlockSize-1] = 1
	} else {
		var y gcmFieldElement
		g.update(&y, nonce)
		y.high ^= uint64(len(nonce)) * 8
		g.mul(&y)
		BePutUint64(counter[:8], y.low)
		BePutUint64(counter[8:], y.high)
	}
	return counter
}
func (g *gcm) GetAuthenticationTag(ciphertext, additionalData []byte, counter []byte) []byte {
	tagMask := make([]byte, gcmBlockSize)
	g.cipher.Encrypt(tagMask, counter)
	var y gcmFieldElement
	g.update(&y, additionalData)
	g.update(&y, ciphertext)
	y.low ^= uint64(len(additionalData)) * 8
	y.high ^= uint64(len(ciphertext)) * 8
	g.mul(&y)
	tag := make([]byte, gcmBlockSize)
	BePutUint64(tag, y.low)
	BePutUint64(tag[8:], y.high)
	subtle.XORBytes(tag, tag, tagMask)
	return tag
}
func counterInc(counterBlock *[gcmBlockSize]byte) {
	ctr := counterBlock[len(counterBlock)-4:]
	BePutUint32(ctr, BeUint32(ctr)+1)
}
type Block interface {
	Encrypt(dst, src []byte)
}
type gcmFieldElement struct {
	low, high uint64
}
type gcm struct {
	cipher       Block
	productTable [16]gcmFieldElement
}
func reverseBits(i int) int {
	i = ((i << 2) & 0xc) | ((i >> 2) & 0x3)
	i = ((i << 1) & 0xa) | ((i >> 1) & 0x5)
	return i
}
var gcmReductionTable = []uint16{
	0x0000, 0x1c20, 0x3840, 0x2460, 0x7080, 0x6ca0, 0x48c0, 0x54e0,
	0xe100, 0xfd20, 0xd940, 0xc560, 0x9180, 0x8da0, 0xa9c0, 0xb5e0,
}
func gcmAdd(x, y *gcmFieldElement) gcmFieldElement {
	return gcmFieldElement{x.low ^ y.low, x.high ^ y.high}
}
func gcmDouble(x *gcmFieldElement) (double gcmFieldElement) {
	msbSet := x.high&1 == 1
	double.high = x.high >> 1
	double.high |= x.low << 63
	double.low = x.low >> 1
	if msbSet {
		double.low ^= 0xe100000000000000
	}
	return
}
func (g *gcm) mul(y *gcmFieldElement) {
	var z gcmFieldElement
	for i := 0; i < 2; i++ {
		word := y.high
		if i == 1 {
			word = y.low
		}
		for j := 0; j < 64; j += 4 {
			msw := z.high & 0xf
			z.high >>= 4
			z.high |= z.low << 60
			z.low >>= 4
			z.low ^= uint64(gcmReductionTable[msw]) << 48
			t := &g.productTable[word&0xf]
			z.low ^= t.low
			z.high ^= t.high
			word >>= 4
		}
	}
	*y = z
}
func (g *gcm) update(y *gcmFieldElement, data []byte) {
	fullBlocks := (len(data) >> 4) << 4
	g.updateBlocks(y, data[:fullBlocks])
	if len(data) != fullBlocks {
		var partialBlock [gcmBlockSize]byte
		copy(partialBlock[:], data[fullBlocks:])
		g.updateBlocks(y, partialBlock[:])
	}
}
func (g *gcm) updateBlocks(y *gcmFieldElement, blocks []byte) {
	for len(blocks) > 0 {
		y.low ^= BeUint64(blocks)
		y.high ^= BeUint64(blocks[8:])
		g.mul(y)
		blocks = blocks[gcmBlockSize:]
	}
}
func BeUint32(b []byte) uint32 {
	_ = b[3]
	return uint32(b[3]) | uint32(b[2])<<8 | uint32(b[1])<<16 | uint32(b[0])<<24
}
func BePutUint32(b []byte, v uint32) {
	_ = b[3]
	b[0] = byte(v >> 24)
	b[1] = byte(v >> 16)
	b[2] = byte(v >> 8)
	b[3] = byte(v)
}
func BeUint64(b []byte) uint64 {
	_ = b[7]
	return uint64(b[7]) | uint64(b[6])<<8 | uint64(b[5])<<16 | uint64(b[4])<<24 |
		uint64(b[3])<<32 | uint64(b[2])<<40 | uint64(b[1])<<48 | uint64(b[0])<<56
}
func BePutUint64(b []byte, v uint64) {
	_ = b[7]
	b[0] = byte(v >> 56)
	b[1] = byte(v >> 48)
	b[2] = byte(v >> 40)
	b[3] = byte(v >> 32)
	b[4] = byte(v >> 24)
	b[5] = byte(v >> 16)
	b[6] = byte(v >> 8)
	b[7] = byte(v)
}

package main
import (
	"context"
	"fmt"
	"runtime"
	"sync"
	"time"
)
func main() {
	ctx, cancel := context.WithTimeout(context.Background(), 1200*time.Millisecond)
	defer cancel()
	input := generate(ctx, 1_000)
	multiplied := process(ctx, 15, input, func(ctx context.Context, val int) (int, error) {
		select {
		case <-ctx.Done():
			return 0, ctx.Err()
		case <-time.After(time.Second):
			return val * 2, nil
		}
	})
	increased := process(ctx, 15, multiplied, func(_ context.Context, val int) (int, error) {
		return val + 10, nil
	})
	fmt.Println("Result: ", sum(increased))                //  360 is ok
	fmt.Println("Num goroutine: ", runtime.NumGoroutine()) // 18 is too much
}
func generate(ctx context.Context, amount int) <-chan int {
	input := make(chan int)
	go func() {
		defer close(input)
		for i := 0; i < amount; i++ {
			select {
			case <-ctx.Done():
				return
			case input <- i:
			}
		}
	}()
	return input
}
func sum(input <-chan int) int {
	result := 0
	for el := range input {
		result += el
	}
	return result
}
func process[T any, R any](ctx context.Context, workers int, input <-chan T, do func(context.Context, T) (R, error)) <-chan R {
	wg := new(sync.WaitGroup)
	result := make(chan R)
	for i := 0; i < workers; i++ {
		wg.Add(1)
		go func() {
			defer wg.Done()
			for {
				select {
				case <-ctx.Done():
					return
				case val, ok := <-input:
					if !ok {
						return
					}
					r, err := do(ctx, val)
					if err != nil {
						return
					}
					result <- r
				}
			}
		}()
	}
	go func() {
		defer close(result)
		wg.Wait()
	}()
	return result
}

type TagParser struct {
	// 1. use [] to collect all fields that have the same key
	// 2. use reflect.Value so that you can utilize f.Set()
	tags map[string][]reflect.Value
}
func ParseTags(tag string, data interface{}) map[string][]reflect.Value {
	var p TagParser
	p.tags = make(map[string][]reflect.Value)
	p.parse(tag, reflect.ValueOf(data))
	return p.tags
}
func (p *TagParser) parse(tag string, rv reflect.Value) {
	if rv.Kind() == reflect.Ptr {
		rv = rv.Elem()
	}
	if rv.Kind() != reflect.Struct {
		return
	}
	rt := rv.Type()
	for i := 0; i < rv.NumField(); i++ {
		f := rv.Field(i)
		if k := rt.Field(i).Tag.Get(tag); k != "" {
			p.tags[k] = append(p.tags[k], f)
		}
		p.parse(tag, f)
	}
}

package main
import (
	"bytes"
	"fmt"
	v1 "k8s.io/api/core/v1"
	"k8s.io/client-go/kubernetes"
	"k8s.io/client-go/tools/clientcmd"
	"k8s.io/client-go/tools/remotecommand"
	"k8s.io/kubectl/pkg/scheme"
)
func executeCommandInPod(kubeconfigPath, pod, namespace, command string) (string, string, error) {
	// Build kubeconfig from the provided path
	config, err := clientcmd.BuildConfigFromFlags("", kubeconfigPath)
	if err != nil {
		return "", "", fmt.Errorf("failed to build kubeconfig: %v", err)
	}
	// Create a new clientset based on the provided kubeconfig
	clientset, err := kubernetes.NewForConfig(config)
	if err != nil {
		return "", "", fmt.Errorf("failed to create clientset: %v", err)
	}
	// Get the pod's name and namespace
	podName := pod
	podNamespace := namespace
	// Build the command to be executed in the pod
	cmd := []string{"sh", "-c", command}
	// Execute the command in the pod
	req := clientset.CoreV1().RESTClient().Post().
		Resource("pods").
		Name(podName).
		Namespace(podNamespace).
		SubResource("exec").
		VersionedParams(&v1.PodExecOptions{
			Command: cmd,
			Stdin:   false,
			Stdout:  true,
			Stderr:  true,
			TTY:     false,
		}, scheme.ParameterCodec)
	executor, err := remotecommand.NewSPDYExecutor(config, "POST", req.URL())
	if err != nil {
		return "", "", fmt.Errorf("failed to create executor: %v", err)
	}
	var stdout, stderr bytes.Buffer
	err = executor.Stream(remotecommand.StreamOptions{
		Stdout: &stdout,
		Stderr: &stderr,
		Tty:    false,
	})
	if err != nil {
		return "", "", fmt.Errorf("failed to execute command in pod: %v", err)
	}
	return stdout.String(), stderr.String(), nil
}
func main() {
	stdout, stderr, err := executeCommandInPod("/tmp/config", "nginx-0", "default", "ls /")
	fmt.Println(stdout, stderr, err)
}

