type AzureEnvInfo struct {
	SubscriptionID  string
	ResourceGrpName string
	VMName          string
}
func DetachAllDataDisks(azureEnvInfo AzureEnvInfo, cred *azidentity.DefaultAzureCredential) error {
	ctx := context.Background()
	vmClient, err := armcompute.NewVirtualMachinesClient(azureEnvInfo.SubscriptionID, cred, nil)
	if err != nil {
		return fmt.Errorf("failed to create VM client: %v", err)
	}
	vm, err := vmClient.Get(ctx, azureEnvInfo.ResourceGrpName, azureEnvInfo.VMName, nil)
	if err != nil {
		return fmt.Errorf("failed to get VM: %w", err)
	}
	for _, disk := range vm.Properties.StorageProfile.DataDisks {
		if disk.Name != nil {
			fmt.Printf("Detaching Disk: %s from VM: %s\n", *disk.Name, azureEnvInfo.VMName)
		}
	}
	vm.Properties.StorageProfile.DataDisks = []*armcompute.DataDisk{}
	DataDisks
	vmUpdate := armcompute.VirtualMachineUpdate{
		Properties: &armcompute.VirtualMachineProperties{
			StorageProfile: &armcompute.StorageProfile{
				DataDisks: vm.Properties.StorageProfile.DataDisks,
			},
		},
	}
	poller, err := vmClient.BeginUpdate(ctx, azureEnvInfo.ResourceGrpName, azureEnvInfo.VMName, vmUpdate, nil)
	if err != nil {
		return fmt.Errorf("failed to begin VM update: %v", err)
	}
	_, err = poller.PollUntilDone(ctx, nil)
	if err != nil {
		return fmt.Errorf("failed to update VM: %v", err)
	}
	fmt.Println("All data disks detached successfully.")
	return nil
}
