func transformQuotes(input string) string {
	var result strings.Builder
	inSingleQuote := false
	startIndex := 0
	for i, r := range input {
		switch r {
		case '\'':
			if inSingleQuote {
				// Closing a single-quoted section
				result.WriteString(`"`)
				innerContent := input[startIndex+1 : i]
				// Replace inner double quotes with single quotes
				result.WriteString(strings.ReplaceAll(innerContent, `"`, `'`))
				result.WriteString(`"`)
				inSingleQuote = false
				startIndex = i + 1
			} else {
				// Opening a single-quoted section
				inSingleQuote = true
				// Add everything up to this point, not handled inside single quotes
				result.WriteString(input[startIndex:i])
				startIndex = i
			}
		}
	}
	// Append any remaining part of the string
	if startIndex < len(input) {
		result.WriteString(input[startIndex:])
	}
	return result.String()
}

