	if dbHOST == "host.docker.internal" {
		tlsOpt = "skip-verify"
	}
	dsn := fmt.Sprintf("%s:%s@tcp(%s:3306)/%s?charset=utf8mb4&parseTime=True&tls=%s&loc=Local", dbCred.Username, dbCred.Password, dbHOST, dbNAME, tlsOpt)
	customDb, err := sql.Open("mysql", dsn)
