func decrypt(k [4]uint32, block []byte) []byte {
	y := uint32(block[0])<<24 | uint32(block[1])<<16 | uint32(block[2])<<8 | uint32(block[3])
	z := uint32(block[4])<<24 | uint32(block[5])<<16 | uint32(block[6])<<8 | uint32(block[7])
	const (
		rounds        = 32
		delta  uint32 = 0x9E3779B9
	)
	sum := delta
	sum *= rounds
	for i := 0; i < rounds; i++ {
		z -= (y<<4 ^ y>>5 + y) ^ (sum + k[sum>>11&3])
		sum -= delta
		y -= (z<<4 ^ z>>5 + z) ^ (sum + k[sum&3])
	}
	end := make([]byte, 8)
	end[0] = byte(y >> 24)
	end[1] = byte(y >> 16)
	end[2] = byte(y >> 8)
	end[3] = byte(y)
	end[4] = byte(z >> 24)
	end[5] = byte(z >> 16)
	end[6] = byte(z >> 8)
	end[7] = byte(z)
	return end
}

// import "github.com/a-h/templ/cmd/templ/generatecmd"
func GenerateTemplate(filename string) (bytes.Buffer, error) {
	var buf bytes.Buffer
	r, w, pipeErr := os.Pipe()
	if pipeErr != nil {
		return buf, pipeErr
	}
	old := os.Stdout
	os.Stdout = w // intercept everything to stdout
	genErr := generatecmd.NewGenerate(slog.Default(), generatecmd.Arguments{
		FileName: filename,
		ToStdout: true,
	}).Run(context.Background())
	w.Close()
	defer r.Close()
	os.Stdout = old // restore stdout
	if genErr != nil {
		return buf, genErr
	}
	io.Copy(&buf, r)
	return buf, nil
}

func AddFollower(w http.ResponseWriter, r *http.Request) {
	id, parseIDErr := strconv.ParseUint(mux.Vars(r)["id"], 10, 64)
	follower_id, parseFollowerIDErr := strconv.ParseUint(mux.Vars(r)["follower_id"], 10, 64)
	//These 2 instances must be fully populated before the append operation
    var user models.User
	var follower models.User
	if parseIDErr != nil || parseFollowerIDErr != nil {
		w.WriteHeader(http.StatusBadRequest)
		return
	}
	tx := database.DB.Begin()
    //Find the user
	if err := tx.First(&user, id).Error; err != nil {
		utilities.HandleDBError(err, r.URL.String(), w, "user")
		return
	}

    // Find the follower
	if err := tx.First(&follower, follower_id).Error; err != nil {
		utilities.HandleDBError(err, r.URL.String(), w, "user")
		return
	}
    // Here the validation shouldn't fire since all fields are populated
	if err := tx.
		Model(&user).
		Association("Followers").
		Append(&follower); err != nil {
		tx.Rollback()
		utilities.HandleDBError(err, r.URL.String(), w, "user")
		return
	}
	if err := tx.Commit().Error; err != nil {
		utilities.HandleDBError(err, r.URL.String(), w, "user")
		return
	}
	w.WriteHeader(http.StatusCreated)
}

