import (
	"crypto/hmac"
	"crypto/sha1"
	"encoding/base64"
	"fmt"
	"log"
	"io"
	"net/http"
	"net/url"
	"strconv"
	"time"
)
func main() {
	consumerKey := "yourkeyhere"
	consumerSecret := "yoursecrethere"
	uri := "https://youruri.com/api"
	now := time.Now().Unix()
	nowStr := strconv.FormatInt(int64(now), 10)
	// spec says nonce is supposed to be unique per request
	nonce := "aAbBcCDd98765axb9"
	form := url.Values{}
	form.Set("oauth_consumer_key", consumerKey)
	form.Set("oauth_nonce", nonce)
	form.Set("oauth_signature_method", "HMAC-SHA1")
	form.Set("oauth_timestamp", nowStr)
	form.Set("oauth_version", "1.0")
	// The first part of my baseString is POST because I'm doing a POST
	// If yours is a GET, then you'd replace it with GET
	baseString := fmt.Sprintf("POST&%s&%s", url.QueryEscape(uri), url.QueryEscape(form.Encode()))
	// spec says the key for the signature should be secret + "&" + token,
    // but if you don't have a token then it's just secret + "&"
	sig := getSignature(baseString, consumerSecret+"&")
	form.Set("oauth_signature", sig)
    // if you're doing a GET request, then you wouldn't use the PostForm method
	resp, err := http.PostForm(uri, form)
	check(err)
	defer resp.Body.Close()
	body, _ := io.ReadAll(resp.Body)
	log.Printf("%d\n%s", resp.StatusCode, body)
}
func getSignature(input, key string) string {
	key_for_sign := []byte(key)
	h := hmac.New(sha1.New, key_for_sign)
	h.Write([]byte(input))
	return base64.StdEncoding.EncodeToString(h.Sum(nil))
}
func check(err error) {
	if err != nil {
		log.Fatal(err)
	}
}

